INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-singapore-2020-2029-lee-kuan-yew-legacy', 'Сингапур 2020-2029 — «Наследие Ли Куан Ю»', 'Игрок изучает ключевые реформы, сталкивается с дилеммой свобода vs контроль и формирует собственное заключение.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-singapore-2020-2029-lee-kuan-yew-legacy", "title": "Сингапур 2020-2029 — «Наследие Ли Куан Ю»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-23T01:50:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["singapore", "quest", "political-history"], "topics": ["nation-building", "governance"], "related_systems": ["narrative-service", "social-service", "education-service"], "related_documents": [{"id": "github-issue-978", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/978", "relation": "migrated_to", "migrated_at": "2025-11-23T01:50:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/singapore/2020-2029/quest-009-lee-kuan-yew-legacy.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Политическая ветка Сингапура не раскрывала путь Ли Куан Ю и системность его реформ.", "goal": "Создать исторический квест с анализом достижений, критики и ценностей отца-основателя.", "essence": "Игрок изучает ключевые реформы, сталкивается с дилеммой свобода vs контроль и формирует собственное заключение.", "key_points": ["Независимость от Малайзии и экономическое чудо в течение 30 лет.", "Антикоррупционная политика и строгие законы как часть стратегии.", "Авторитарные меры вызывают спор о цене процветания."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест начинается в национальной галерее. Игрок получает биографическую ленту, аудиогид и список ключевых реформ для анализа.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "achievements", "title": "Достижения", "body": "Игрок изучает жилищную программу HDB, привлечение инвестиций и образовательные реформы, сопоставляя их с метриками успеха.\\n", "mechanics_links": ["mechanics/progression/economic-systems.yaml"], "assets": []}, {"id": "criticism", "title": "Авторитарная критика", "body": "Рассматриваются кейсы цензуры, преследования оппозиции и ограничения свобод. Игрок должен собрать мнения разных NPC.\\n", "mechanics_links": ["mechanics/narrative/branching-decisions.yaml"], "assets": []}, {"id": "decision", "title": "Итоговая оценка", "body": "Финальный шаг — публичное выступление или отчёт, где игрок выбирает свою позицию (поддержка/критика/баланс), влияющую на политическую репутацию.\\n", "mechanics_links": ["mechanics/social/political-reputation.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 4 000 XP, политика +25, финансовые затраты отсутствуют.\\n- Ачивка «Знаток истории Сингапура» и доступ к веткам гражданских инициатив.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест интегрируется с сюжетами «Строгие законы», инфраструктурными успехами и глобальными линиями модернизации городов.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 978, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Наследие Ли Куан Ю» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

