INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-moscow-social-credit', 'Москва 2030-2039 — «Социальный кредит»', 'Игрок живёт под контролем рейтинга Московской архологии и решает — принять, взломать или уничтожить систему.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-moscow-social-credit", "title": "Москва 2030-2039 — «Социальный кредит»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T03:20:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "lore_analyst", "contact": "lore@necp.game"}], "tags": ["moscow", "social-credit", "surveillance"], "topics": ["societal-control", "branching-narrative"], "related_systems": ["narrative-service", "reputation-system", "event-system"], "related_documents": [{"id": "canon-quest-moscow-dome-charter", "relation": "builds_on"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/moscow/2030-2039/quest-020-social-credit.md", "visibility": "internal", "audience": ["lore", "narrative", "systems"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Черновик описывал систему социального кредита без формального разветвления последствий.", "goal": "Сформировать квест внедрения рейтинговой системы с тремя ключевыми выборами.", "essence": "Игрок живёт под контролем рейтинга Московской архологии и решает — принять, взломать или уничтожить систему.", "key_points": ["Рейтинг влияет на доступ и льготы.", "Расследование раскрывает управление системой.", "Три финала формируют мировое событие."]}, "content": {"sections": [{"id": "announcement", "title": "Анонс и первые шаги", "body": "Игрок получает стартовый рейтинг, наблюдает, как система внедряется. Объясняются бонусы и штрафы.\\n", "mechanics_links": [], "assets": []}, {"id": "lifestyle", "title": "Жизнь с рейтингом", "body": "Квесты, преступления и помощь жителям меняют рейтинг. Высокие показатели дают доступ к элитным зонам, низкие — приводят к санкциям.\\n", "mechanics_links": [], "assets": []}, {"id": "investigation", "title": "Расследование", "body": "Игрок выясняет, кто контролирует систему (проверка Взлом DC 20), собирает данные и готовит решение.\\n", "mechanics_links": [], "assets": []}, {"id": "choices", "title": "Финальный выбор", "body": "- Принять: жить с системой, максимизировать рейтинг и получать бонусы.\\n- Взломать: проверка DC 22, мгновенно максимизирует рейтинг, но несёт риск разоблачения.\\n- Саботировать: уничтожить серверы, +10 к репутации народа, -15 к власти, розыск, но свобода.\\n", "mechanics_links": [], "assets": []}, {"id": "outcomes", "title": "Награды и последствия", "body": "2 500 XP, валютные и репутационные эффекты зависят от выбора. Итог регистрируется как мировое событие в Москве.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 273, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "lore_team", "changes": "Конверсия квеста «Социальный кредит» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

