INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-singapore-2020-2029-water-independence', 'Сингапур 2020-2029 — «Водная независимость»', 'Игрок изучает историю водной зависимости, посещает завод NEWater и оценивает роль технологий в обеспечении независимости.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-singapore-2020-2029-water-independence", "title": "Сингапур 2020-2029 — «Водная независимость»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-23T01:50:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["singapore", "quest", "water-security"], "topics": ["sustainability", "infrastructure"], "related_systems": ["narrative-service", "ecology-service", "innovation-service"], "related_documents": [{"id": "github-issue-979", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/979", "relation": "migrated_to", "migrated_at": "2025-11-23T01:50:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/singapore/2020-2029/quest-010-water-independence.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Технологическая линия Сингапура не показывала проект водной независимости и экосистему NEWater.", "goal": "Создать образовательный квест с посещением объектов водоснабжения, демонстрацией инноваций и геополитического контекста.", "essence": "Игрок изучает историю водной зависимости, посещает завод NEWater и оценивает роль технологий в обеспечении независимости.", "key_points": ["Сингапур перешёл от импорта воды к собственной многоуровневой системе.", "NEWater и опреснение стали ключевыми решениями.", "Квест связывает экологию с инфраструктурой и безопасностью."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Игрок начинает в Информационном центре PUB, получает вводную о зависимости от Малайзии и планах по достижению водной автономии.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "history", "title": "Исторический контекст", "body": "Раздел охватывает соглашения с Малайзией, кризисы поставок и политическую мотивацию к созданию собственной системы.\\n", "mechanics_links": ["mechanics/lore/historical-analysis.yaml"], "assets": []}, {"id": "newater", "title": "Завод NEWater", "body": "Игрок посещает завод, изучает процессы ультрафильтрации и мембранного опреснения, участвует в демонстрации и дегустирует воду.\\n", "mechanics_links": ["mechanics/innovation/industrial-tour.yaml"], "assets": []}, {"id": "desalination", "title": "Опреснение и резервуары", "body": "Раздел описывает роль морских опреснительных заводов и городских водосборов. Игрок планирует распределение ресурсов для разных сценариев.\\n", "mechanics_links": ["mechanics/ecology/resource-management.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 3 000 XP, наука +20, финансовых затрат нет.\\n- Ачивка «Хранитель воды» и доступ к экологическим проектам региона.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связывает технологическую и экологическую ветки Сингапура с глобальными сюжетами устойчивых городов.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 979, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Водная независимость» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

