INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-moscow-2061-2077-simulation-hint', 'Москва 2061-2077 — «Край симуляции»', 'Игрок фиксирует аномалии Москвы, складывает координаты и находит терминал, подтверждающий симуляцию.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-moscow-2061-2077-simulation-hint", "title": "Москва 2061-2077 — «Край симуляции»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T05:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "lore_analyst", "contact": "lore@necp.game"}], "tags": ["moscow", "simulation", "mystery"], "topics": ["main-arc", "psychological-thriller"], "related_systems": ["narrative-service", "world-state-system", "quest-engine"], "related_documents": [{"id": "canon-region-cis-moscow-2040-2093", "relation": "contextualizes"}, {"id": "canon-quest-moscow-2061-2077-radio-silence", "relation": "connected"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/moscow/2061-2077/quest-037-simulation-hint.md", "visibility": "internal", "audience": ["lore", "narrative", "systems"], "risk_level": "critical"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Черновик раскрывал симуляцию без сцен, точек аномалий и влияния на сюжетные ветки.", "goal": "Сконструировать экстремальный сюжетный квест, ведущий к знанию о симуляции.", "essence": "Игрок фиксирует аномалии Москвы, складывает координаты и находит терминал, подтверждающий симуляцию.", "key_points": ["Квест-id MOSCOW-2077-037, тип main, сложность extreme, длительность 4+ часов.", "Десять аномальных точек формируют координаты бункера.", "Выбор принятия истины запускает линию «Выход из симуляции»."]}, "content": {"sections": [{"id": "anomalies", "title": "Отслеживание аномалий", "body": "Игрок замечает повторяющихся NPC и глюки окружения. Система world-state фиксирует отклонения, выдаёт журнал с десятью координатами.\\n", "mechanics_links": [], "assets": []}, {"id": "investigation", "title": "Расследование и паттерн", "body": "Анализ улик через терминалы и беседы с очевидцами открывает карту города. Все аномалии образуют рисунок, ведущий к заброшенному бункеру.\\n", "mechanics_links": [], "assets": []}, {"id": "revelation", "title": "Открытие терминала", "body": "Внутри бункера игрок находит старый сервер. Лог раскрывает сообщение «Вы в симуляции» и предлагает решения: принять, отвергнуть или сообщить властям.\\n", "mechanics_links": [], "assets": []}, {"id": "consequences", "title": "Последствия выбора", "body": "- Принять истину: открывает ветку «Выход из симуляции».\\n- Отвергнуть: квест закрывается, игрок получает временный бафф спокойствия.\\n- Сообщить властям: запускает сцену преследования.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 275, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "lore_team", "changes": "Конверсия квеста «Край симуляции» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

