INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-2020-2029-yakuza-initiation', 'Токио 2020-2029 — «Инициация в якудза»', 'Игрок выполняет поручение вакагасиры, проходит церемонию и получает кибер-татуировку клана.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-2020-2029-yakuza-initiation", "title": "Токио 2020-2029 — «Инициация в якудза»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["tokyo", "quest", "yakuza"], "topics": ["faction-initiation", "organized-crime"], "related_systems": ["narrative-service", "faction-service", "combat-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/tokyo/2020-2029/quest-002-yakuza-initiation.md", "visibility": "restricted", "audience": ["concept", "narrative"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Фракционная линия Токио требовала детального вступительного квеста для клана якудза.", "goal": "Определить церемонию и испытания, демонстрирующие традиции и необратимость вступления.", "essence": "Игрок выполняет поручение вакагасиры, проходит церемонию и получает кибер-татуировку клана.", "key_points": ["Ритуалы yubitsume и иредзуми поддерживают аутентичность.", "Вступление открывает доступ к криминальным контрактам и защите клана.", "Решение необратимо и конфликтует с полицией и конкурирующими группировками."]}, "content": {"sections": [{"id": "briefing", "title": "Знакомство и поручение", "body": "Игрок встречается с вакагасирой, получает задачу по сбору долга и выбирает тактику переговоров или давления.\\n", "mechanics_links": ["mechanics/factions/territory-control.yaml"], "assets": []}, {"id": "ceremony", "title": "Церемония инициации", "body": "В офисе клана проходит поклон, обмен саке, возможное yubitsume и нанесение кибер-татуировки, фиксирующее принадлежность.\\n", "mechanics_links": ["mechanics/narrative/ritual-events.yaml"], "assets": []}, {"id": "aftermath", "title": "Последствия членства", "body": "Игрок получает статус члена, доступ к контрактам, но сталкивается с трекингом полиции и обязанностями клана.\\n", "mechanics_links": ["mechanics/factions/faction-contracts.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 2 500 XP, 5 000 Eddies, доступ к якудза-контрактам, скидки у поставщиков -30%.\\n- Репутация полиции резко падает, выход из фракции невозможен.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест запускает линию из 20+ криминальных миссий Токио и влияет на баланс сил между фракциями.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 952, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Инициация в якудза» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

