INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-2020-2029-tokyo-tower-hack', 'Токио 2020-2029 — «Взлом Токийской башни»', 'Игрок проникает на башню, взламывает центральный узел и выбирает, отключить рекламу, монетизировать её или устроить хаос.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-2020-2029-tokyo-tower-hack", "title": "Токио 2020-2029 — «Взлом Токийской башни»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["tokyo", "quest", "hacking"], "topics": ["infiltration", "media-control"], "related_systems": ["narrative-service", "combat-service", "world-state-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/tokyo/2020-2029/quest-005-tokyo-tower-hack.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Медиалиния Токио нуждалась в ключевом событии по захвату AR-инфраструктуры города.", "goal": "Описать основной квест, где игрок взламывает Tokyo Tower и решает судьбу рекламной сети.", "essence": "Игрок проникает на башню, взламывает центральный узел и выбирает, отключить рекламу, монетизировать её или устроить хаос.", "key_points": ["Выбор исхода влияет на визуальный облик и экономику Токио.", "Квест сочетает стелс, хакерство и вертикальный паркур.", "Результат отражается на репутации с фракциями и LiveOps событиях."]}, "content": {"sections": [{"id": "infiltration", "title": "Проникновение", "body": "Игрок нанимает хакера, выбирает подход (стелс, социальная инженерия или силовой) и поднимается по 333-метровой башне.\\n", "mechanics_links": ["mechanics/infiltration/vertical-infiltration.yaml"], "assets": []}, {"id": "hacking", "title": "Взлом центрального узла", "body": "На вершине необходимо провести серию мини-игр по взлому, обходя системы безопасности и удерживая контроль над башней.\\n", "mechanics_links": ["mechanics/hacking/network-intrusion.yaml"], "assets": []}, {"id": "choices", "title": "Выбор исхода", "body": "Игрок решает: отключить рекламу, захватить контроль или устроить саботаж. Каждый вариант меняет город и открывает последующие миссии.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 3 000 XP, опциональный доход до 10 000 Eddies/неделя, изменение состояния города.\\n- Репутация фракций обновляется в зависимости от выбранного исхода.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест влияет на визуальное LiveOps оформление Токио и связывает сюжетные линии курьеров, медиа-корпораций и сопротивления.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 955, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Взлом Токийской башни» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

