INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-moscow-corpo-espionage', 'Москва 2030-2039 — «Корпоративный шпионаж»', 'Игрок внедряется в корпорацию-конкурента, крадёт данные и решает, кому их продать.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-moscow-corpo-espionage", "title": "Москва 2030-2039 — «Корпоративный шпионаж»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T03:20:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "lore_analyst", "contact": "lore@necp.game"}], "tags": ["moscow", "espionage", "corporations"], "topics": ["covert-ops", "faction-play"], "related_systems": ["narrative-service", "reputation-system", "loot-system"], "related_documents": [{"id": "canon-region-cis-moscow-2030-2093", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/moscow/2030-2039/quest-018-corpo-espionage.md", "visibility": "internal", "audience": ["lore", "narrative", "systems"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Черновик не формализовал этапы шпионской операции и последствия выбора агента.", "goal": "Структурировать квест корп-шпионажа с вариантами поведения (честный, двойной, тройной агент).", "essence": "Игрок внедряется в корпорацию-конкурента, крадёт данные и решает, кому их продать.", "key_points": ["Выбор стратегии (обман, взлом, силовой выход).", "Возможность двойной/тройной игры с высокими рисками.", "Награды масштабируются с ростом опасности."]}, "content": {"sections": [{"id": "briefing", "title": "Найм и подготовка", "body": "Корпорация нанимает игрока, предоставляет легенду и инструменты. Игрок выбирает подход: внедрение или удалённый взлом.\\n", "mechanics_links": [], "assets": []}, {"id": "infiltration", "title": "Внедрение", "body": "Проверки Обмана DC 18, социальная инженерия, либо хакерский вход. Игрок собирает доступы и избегает систем безопасности.\\n", "mechanics_links": [], "assets": []}, {"id": "data_heist", "title": "Кража данных", "body": "Игрок находит проекты, патенты и исследования. Возможны мини-игры по взлому и обходу охраны.\\n", "mechanics_links": [], "assets": []}, {"id": "extraction", "title": "Эвакуация и сдача", "body": "Уход скрытно или с боем. Дальше игрок выбирает, кому передать данные: нанимателю, обеим корпорациям или третьей стороне.\\n", "mechanics_links": [], "assets": []}, {"id": "outcomes", "title": "Исходы", "body": "- Честный агент: 2 000 XP, 15 000 едди, репутация +3.\\n- Двойной агент: 3 000 XP, 30 000 едди, риск понижения репутации (-5 при провале).\\n- Тройной агент: 4 000 XP, 50 000 едди, розыск.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 273, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "lore_team", "changes": "Конверсия квеста «Корпоративный шпионаж» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

