INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-2020-2029-capsule-hotel', 'Токио 2020-2029 — «Ночь в капсульном отеле»', 'Игрок заселяется в капсулу, изучает общие сервисы и получает восстановление после рабочей смены.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-2020-2029-capsule-hotel", "title": "Токио 2020-2029 — «Ночь в капсульном отеле»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["tokyo", "quest", "capsule-hotel"], "topics": ["lifestyle", "urban-living"], "related_systems": ["narrative-service", "hospitality-service", "wellbeing-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/tokyo/2020-2029/quest-006-capsule-hotel.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Городская линия Токио не демонстрировала опыт бюджетного ночлега в капсульных отелях.", "goal": "Добавить камерный квест отдыха с акцентом на минимализм, общие зоны и атмосферу салариманов.", "essence": "Игрок заселяется в капсулу, изучает общие сервисы и получает восстановление после рабочей смены.", "key_points": ["Капсульный отель представляет японский минимализм и плотную городскую инфраструктуру.", "Общие зоны с сэнто и лаунжами создают социальные микроистории.", "Квест поддерживает экономичный туризм и лайфстайл-события Токио."]}, "content": {"sections": [{"id": "arrival", "title": "Заселение", "body": "Игрок бронирует капсулу, проходит процедуру снятия обуви и получает ключ-карту. Система поясняет правила поведения и расписание\\nобщих зон.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "capsule", "title": "Ночь в капсуле", "body": "Внутри капсулы игрок активирует свет, вентиляцию и медиапанель. Шумы соседей и вибрации поездов формируют атмосферный опыт.\\n", "mechanics_links": ["mechanics/wellbeing/rest-system.yaml"], "assets": []}, {"id": "sento", "title": "Общая баня", "body": "Посещение сэнто даёт бонусы восстановления. Игрок встречает NPC-салариманов, узнаёт их истории и получает локальные слухи.\\n", "mechanics_links": ["mechanics/social/lounge-interactions.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 300 XP, полное восстановление HP, расходы 500 еддис.\\n- Ачивка «Жизнь саларимана» и доступ к событиям экономичного жилья.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связывает бытовую ветку Токио с транспортными и ночными событиями мегаполиса.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 956, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Отель-капсула» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTA