INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-2020-2029-shrine-offering', 'Токио 2020-2029 — «Приношение в храме Мэйдзи»', 'Игрок проходит через тории Мэйдзи-дзингу, совершает ритуал и получает случайный бафф или вызов.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-2020-2029-shrine-offering", "title": "Токио 2020-2029 — «Приношение в храме Мэйдзи»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-23T01:50:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["tokyo", "quest", "shrine"], "topics": ["spirituality", "cultural-rituals"], "related_systems": ["narrative-service", "wellbeing-service", "randomness-service"], "related_documents": [{"id": "github-issue-980", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/980", "relation": "migrated_to", "migrated_at": "2025-11-23T01:50:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/tokyo/2020-2029/quest-008-shrine-offering.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Духовная линия Токио не имела короткого синтоистского ритуала с механикой омикудзи.", "goal": "Создать атмосферный квест посещения святилища с очищением, приношением и случайным благословением.", "essence": "Игрок проходит через тории Мэйдзи-дзингу, совершает ритуал и получает случайный бафф или вызов.", "key_points": ["Квест демонстрирует базовые элементы синтоизма и городской духовности.", "Омикудзи добавляет вариативность эффектов и мотивацию повторять визит.", "Связь с другими духовными сюжетами усиливает религиозную ветку региона."]}, "content": {"sections": [{"id": "approach", "title": "Путь к святилищу", "body": "Игрок проходит через лесной подход к Мэйдзи-дзингу, слышит молитвы и наблюдает за торжественными процессиями.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "purification", "title": "Омовение и приношение", "body": "У хандзё игрок очищает руки и рот, затем бросает монету, хлопает и кланяется. Корректное выполнение даёт бонус к удаче.\\n", "mechanics_links": ["mechanics/rituals/purification.yaml"], "assets": []}, {"id": "omikuji", "title": "Омикудзи", "body": "Игрок вытягивает предсказание, развешивает его на стенде в случае плохой судьбы или хранит при благословении.\\n", "mechanics_links": ["mechanics/random/fortune-draw.yaml"], "assets": []}, {"id": "ema", "title": "Дощечки желаний", "body": "На площадке эма игрок может записать собственное желание или прочитать истории других посетителей, открывая духовные побочные задания.\\n", "mechanics_links": ["mechanics/narrative/micro-stories.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 400 XP, бафф «Удача +5% на 24 часа» при хорошем омикудзи, расходы 5 йен.\\n- Ачивка «Благословение ками» и доступ к цепочке духовных квестов.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест стыкуется с духовными сюжетами Киото и региональными праздниками Обон.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 980, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Приношение в храме» в формат знаний."}], "validation": {