INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-2020-2029-pachinko-addiction', 'Токио 2020-2029 — «Зависимость от патинко»', 'Игрок вытаскивает зависимого из патинко-парлора, сталкиваясь с мафиозным контролем и выборами.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-2020-2029-pachinko-addiction", "title": "Токио 2020-2029 — «Зависимость от патинко»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["tokyo", "quest", "pachinko"], "topics": ["social-issues", "addiction"], "related_systems": ["narrative-service", "social-service", "reputation-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/tokyo/2020-2029/quest-010-pachinko-addiction.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Социальная линия Токио не освещала проблему зависимости от патинко и влияние якудза.", "goal": "Создать моральный квест с тремя путями помощи NPC: силой, выкупом или лечением.", "essence": "Игрок вытаскивает зависимого из патинко-парлора, сталкиваясь с мафиозным контролем и выборами.", "key_points": ["Патинко показано как шумный и агрессивный мир с социальной ценой.", "Присутствие якудза добавляет риск и разворачивает боевую ветку.", "Решение игрока влияет на репутацию и будущие социальные сценарии."]}, "content": {"sections": [{"id": "briefing", "title": "Встреча с NPC", "body": "Игрок получает просьбу о помощи от родственницы зависимого. Диалог раскрывает масштаб долгов и угроз.\\n", "mechanics_links": ["mechanics/narrative/dialogue-system.yaml"], "assets": []}, {"id": "parlor", "title": "Патинко-парлор", "body": "Внутри зала игрок сталкивается с шумом, дымом и зомбированными игроками. Необходимо убедить цель покинуть автомат.\\n", "mechanics_links": ["mechanics/social/intervention.yaml"], "assets": []}, {"id": "confrontation", "title": "Столкновение с якудза", "body": "Появляются представители клана, требующие вернуть долг. Игрок выбирает подход: переговоры, оплата или силовой путь.\\n", "mechanics_links": ["mechanics/combat/gang-encounter.yaml"], "assets": []}, {"id": "resolution", "title": "Решение", "body": "Выбор игрока определяет исход: участие в драке, оплата 20 000 еддис или направление NPC в клинику с длительной линией восстановления.\\n", "mechanics_links": ["mechanics/narrative/branching-decisions.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 1 200 XP, изменение репутации в зависимости от метода, возможные затраты 20 000 еддис.\\n- Ачивка «Друг спасён» и доступ к квестам о зависимостях.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связывает социальные линии Токио с международными сюжетами о зависимости и контроле корпораций.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 959, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Зависимость от патинко» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, 