INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-dallas-2029-005-rodeo', 'Даллас — техасское родео', 'Игрок посещает State Fair of Texas, участвует в соревнованиях и укрепляет связи с ковбойской культурой.', 'side', 10, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "buy_equipment_tickets", "text": "Приобрести экипировку и билеты на State Fair of Texas", "type": "interact", "target": "fair_ticket_office", "count": 1, "optional": false}, {"id": "backstage_tour", "text": "Пройти закулисный тур и оценить безопасность площадки", "type": "travel", "target": "rodeo_backstage", "count": 1, "optional": false}, {"id": "watch_bull_riding", "text": "Наблюдать за bull riding и поддержать выбранного участника", "type": "minigame", "target": "bull_riding_event", "count": 1, "optional": false}, {"id": "help_organizers", "text": "Помочь организаторам с crowd-control или развлечением зрителей", "type": "interact", "target": "organizer_assistance", "count": 1, "optional": true}, {"id": "choose_alliance", "text": "Выбрать союз — профессиональные наездники или семейные организаторы фестиваля", "type": "dialogue", "target": "alliance_choice_npc", "count": 1, "optional": false}]'::jsonb, '{"experience": 1500, "money": -40, "reputation": {"culture": 20}, "items": [], "unlocks": {"achievements": ["Ковбой Техаса"], "flags": []}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-dallas-2029-005-rodeo", "title": "Даллас — техасское родео", "document_type": "canon", "category": "quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-12-06T10:50:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["dallas", "rodeo", "culture"], "topics": ["city_storylines", "cultural_events"], "related_systems": ["narrative-service", "event-service", "social-service"], "related_documents": [{"id": "master-timeline", "relation": "references"}, {"id": "quest-system", "relation": "aligns"}, {"id": "github-issue-332", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/332", "relation": "migrated_to", "migrated_at": "2025-11-22T19:45:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/dallas/2020-2029/quest-005-rodeo.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Исходный текст описывал родео, но не указывал, как событие влияет на фракции и социальную динамику игрока.", "goal": "Связать посещение родео со сценами выбора, влияющими на репутацию и сезонные активности.", "essence": "Игрок посещает State Fair of Texas, участвует в соревнованиях и укрепляет связи с ковбойской культурой.", "key_points": ["Родео выступает социальным центром с выбором между спортивной и фольклорной линией.", "Игрок может влиять на безопасность мероприятий и получить доступ к закулисным контактам.", "Событие открывает сезонные активности для фракций фермеров и организаторов ярмарки."]}, "quest_definition": {"quest_type": "side", "level_min": 10, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "buy_equipment_tickets", "text": "Приобрести экипировку и билеты на State Fair of Texas", "type": "interact", "target": "fair_ticket_office", "count": 1, "optional": false}, {"id": "backstage_tour", "text": "Пройти закулисный тур и оценить безопасность площадки", "type": "travel", "target": "rodeo_backstage", "count": 1, "optional": false}, {"id": "watch_bull_riding", "text": "Наблюдать за bull riding и поддержать выбранного участника", "type": "minigame", "target": "bull_riding_event", "count": 1, "optional": false}, {"id": "help_organizers", "text": "Помочь организаторам с crowd-control или развлечением зрителей", "type": "interact", "target": "organizer_assistance", "count": 1, "optional": true}, {"id": "choose_alliance", "text": "Выбрать союз — профессиональные наездники или семейные организаторы фестиваля", "type": "dialogue", "target": "alliance_choice_npc", "count": 1, "optional": false}], "rewards": {"experience": 1500, "money": -40, "reputation": {"culture": 20}, "items": [], "unlocks": {"achievements": ["Ковбой Техаса"], "flags": []}}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест начинается с покупки билетов на родео State Fair of Texas и подготовки экипировки. Игрок наблюдает соревнования,\\nпомогает организаторам и выбирает, поддерживать ли профессиональных наездников или рекламировать семейный формат.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"], "assets": []}, {"id": "regional_highlights", "title": "Региональные отсылки", "body": "Родео включает bull riding, barrel racing и номера с лассо. Традиционные ковбойские шляпы и country-музыка создают атмосферу\\nпраздника, а символ Big Tex закрепляет связь с ярмаркой.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы квеста", "body": "1. Приобрести экипировку и билеты на State Fair of Texas.\\n2. Пройти закулисный тур и оценить безопасность площадки.\\n3. Наблюдать за bull riding и поддержать выбранного участника.\\n4. Помочь организаторам с crowd-control или развлечением зрителей.\\n5. Выбрать союз — профессиональные наездники или семейные организаторы фестиваля.\\n", "mechanics_links": ["mechanics/social/social-mechanics-overview.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Квест даёт 1500 опыта, требует 40 единиц валюты на билеты и атрибутику, увеличивает культурный показатель на 20.\\nОткрывается ачивка «Ковбой Техаса» и сезонные контракты на организацию ярмарок.\\n", "mechanics_links": [], "assets": []}, {"id": "integration", "title": "Интеграции и последствия", "body": "Event-service фиксирует родео как сезонное событие, social-service отслеживает репутацию среди ковбоев и семейных зрителей,\\nа narrative-service обновляет культурную линию города.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1177, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_team", "changes": "Описан квест «Техасское родео» с социальными ветвлениями и сезонными интеграциями."}, {"version": "2.0.0", "date": "2025-12-06", "author": "content_writer", "changes": "Добавлен quest_definition с полной структурой квеста согласно архитектуре системы квестов."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

