INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-moscow-2078-2093-new-beginning', 'Москва 2078-2093 — «Новое начало»', 'Квест подводит итоги 50 заданий Москвы, переносит ключевые награды и запускает перерождение персонажа.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-moscow-2078-2093-new-beginning", "title": "Москва 2078-2093 — «Новое начало»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["moscow", "quest", "finale"], "topics": ["league_transition", "legacy_system"], "related_systems": ["narrative-service", "progression-service", "social-system"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/moscow/2078-2093/quest-050-new-beginning.md", "visibility": "internal", "audience": ["concept", "narrative", "progression"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Игроку нужен эмоциональный финал московской арки и структурированный переход в следующую лигу без потери прогресса.", "goal": "Организовать ритуал завершения сезона с выбором наследуемых элементов и зафиксировать отправную точку новой лиги.", "essence": "Квест подводит итоги 50 заданий Москвы, переносит ключевые награды и запускает перерождение персонажа.", "key_points": ["Legacy-выбор определяет артефакты, визуальные элементы и титулы, переходящие в новую лигу.", "Финальное задание и послание формируют персональную историю игрока.", "Катсцена на крыше архологии закрепляет эмоциональную точку и открывает окно в следующий сезон."]}, "content": {"sections": [{"id": "legacy_selection", "title": "Выбор наследия", "body": "Игроку предлагают сформировать набор legacy-элементов: один артефакт, три скина и автоматически сохраняемые титулы и достижения.\\nВыбор отображает статистику пройденных заданий и влияет на стартовые бонусы следующей лиги.\\n", "mechanics_links": [], "assets": []}, {"id": "farewell_message", "title": "Завещание", "body": "В спокойной сцене персонаж записывает сообщение самому себе в будущем. Можно выбрать тональность, адресатов и ключевые советы.\\nКонтент сообщения хранится и воспроизводится при старте следующей лиги, создавая связность сюжета.\\n", "mechanics_links": [], "assets": []}, {"id": "final_mission", "title": "Последнее испытание", "body": "Игрок завершает персональный квест, связанный с выбранной специализацией. Миссия короткая, но отражает итоговые решения московской арки.\\nНаграда не выражена в числах, но закрепляет статус и усиливает эмоциональный эффект.\\n", "mechanics_links": [], "assets": []}, {"id": "countdown_rebirth", "title": "Обратный отсчёт и перерождение", "body": "В UI запускается обратный отсчёт. Изменение освещения и звуков усиливает напряжение.\\nПосле нулевой отметки включается катсцена: герой смотрит на закат над Москвой, экран перетекает в белый свет и формирует загрузку новой лиги.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 276, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Новое начало» в формат knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

