INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-2030-2039-robot-restaurant', 'Токио 2030-2039 — «Ресторан роботов»', 'Игрок попадает на неоновое шоу, наслаждается выступлением и получает культурные бонусы.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-2030-2039-robot-restaurant", "title": "Токио 2030-2039 — «Ресторан роботов»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["tokyo", "quest", "entertainment"], "topics": ["nightlife", "spectacle"], "related_systems": ["narrative-service", "liveops-events-service", "economy-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/tokyo/2030-2039/quest-014-robot-restaurant.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Туристическая линия Токио не отражала неоновую экстравагантность Robot Restaurant и его влияние на культуру будущего.", "goal": "Добавить короткий лайфстайл-квест с покупкой билета, просмотром шоу и взаимодействием с робо-персонажами.", "essence": "Игрок попадает на неоновое шоу, наслаждается выступлением и получает культурные бонусы.", "key_points": ["Квест демонстрирует сочетание кавайи-эстетики и киберпанка.", "Игрок тратит заметную сумму, получая уникальный культурный опыт.", "Событие подходит для LiveOps кампаний по ночной жизни."]}, "content": {"sections": [{"id": "ticket", "title": "Покупка билета", "body": "Игрок бронирует место, оплачивает вход и проходит через неоновый вестибюль, знакомясь с правилами шоу.\\n", "mechanics_links": ["mechanics/economy/retail-shopping.yaml"], "assets": []}, {"id": "show", "title": "Шоу роботов", "body": "На сцене выступают танцующие роботы, лазеры и барабанщики. Игрок наблюдает кат-сцены и собирает впечатления.\\n", "mechanics_links": ["mechanics/media/performance-tracking.yaml"], "assets": []}, {"id": "interaction", "title": "Взаимодействие после шоу", "body": "Игрок делает селфи с робо-персонажами, получает сувениры и бонусы к культурному рейтингу.\\n", "mechanics_links": ["mechanics/social/social-gatherings.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 500 XP, -3 000 йен, культура +3, ачивка «Неоновое безумие».\\n- Открываются сюжеты о ночной жизни и рекламные LiveOps с участием роботов.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест поддерживает туристическую и развлекательную линию Токио и связывается с рекламными сюжетами мегаполиса.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 963, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Ресторан роботов» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

