INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-2030-2039-ninja-clan', 'Токио 2030-2039 — «Клан ниндзя»', 'Игрок находит тайное додзё, проходит серию экстремальных испытаний и получает кибер-ниндзя способности.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-2030-2039-ninja-clan", "title": "Токио 2030-2039 — «Клан ниндзя»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["tokyo", "quest", "ninja"], "topics": ["stealth", "faction-initiation"], "related_systems": ["narrative-service", "combat-service", "mobility-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/tokyo/2030-2039/quest-015-ninja-clan.md", "visibility": "restricted", "audience": ["concept", "narrative"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Фракционная линия ассасинов Токио не имела сюжетного вступления в современный клан ниндзя.", "goal": "Определить испытания и техники, подтверждающие принадлежность к кибер-ниндзя.", "essence": "Игрок находит тайное додзё, проходит серию экстремальных испытаний и получает кибер-ниндзя способности.", "key_points": ["Испытания включают стелс, убийство без обнаружения и владение оружием.", "Техники нинпо адаптированы под кибер-эстетику.", "Вступление открывает ассасин-контракты и уникальные импланты."]}, "content": {"sections": [{"id": "discovery", "title": "Поиск клана", "body": "Игрок расследует слухи о современных синоби, находит вход в скрытое додзё и проходит допуск.\\n", "mechanics_links": ["mechanics/infiltration/vertical-infiltration.yaml"], "assets": []}, {"id": "trials", "title": "Испытания", "body": "Серия испытаний: стелс на высоком уровне сложности, бесшумное устранение целей и прохождение полосы препятствий.\\n", "mechanics_links": ["mechanics/combat/assassination-trials.yaml"], "assets": []}, {"id": "techniques", "title": "Обучение техникам", "body": "Игрок осваивает набор нинпо (сюнсин, кагэ-буншин, дзё-дзюцу), усиливающих скорость и маскировку.\\n", "mechanics_links": ["mechanics/skills/advanced-stealth.yaml"], "assets": []}, {"id": "initiation", "title": "Церемония инициации", "body": "Финальная церемония включает вручение маски, имплантов и клятву верности клану. Решение необратимо.\\n", "mechanics_links": ["mechanics/narrative/ritual-events.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 5 000 XP, техники нинпо, доступ к ассасин-контрактам, стелс-импланты.\\n- Репутация полиции ухудшается, выход из клана невозможен.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест запускает ассасин-линию Токио, пересекается с глобальными сюжетами скрытности и влияет на LiveOps событийную сетку.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 964, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Клан ниндзя» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives