INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-2030-2039-manga-artist', 'Токио 2030-2039 — «Путь мангаки»', 'Игрок заключает контракт с Weekly Jump, создаёт серию и балансирует творческое вдохновение с выгоранием.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-2030-2039-manga-artist", "title": "Токио 2030-2039 — «Путь мангаки»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["tokyo", "quest", "manga"], "topics": ["creative-career", "publishing"], "related_systems": ["narrative-service", "career-service", "liveops-events-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/tokyo/2030-2039/quest-016-manga-artist.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Городская культурная линия Токио не раскрывала путь мангаки и стресс издательских дедлайнов.", "goal": "Создать карьерный квест с созданием манги, управлением ассистентами и борьбой за рейтинги.", "essence": "Игрок заключает контракт с Weekly Jump, создаёт серию и балансирует творческое вдохновение с выгоранием.", "key_points": ["Выбор жанра (сёнэн, сёдзё, сэйнэн) меняет требования аудитории.", "Мини-игры рисования и монтаж страниц формируют цикл дедлайнов.", "Рейтинговая система журнала определяет доход и продолжение серии."]}, "content": {"sections": [{"id": "pitch", "title": "Подача концепта", "body": "Игрок посещает редакцию Weekly Jump, презентует синопсис и персонажей. Успех зависит от оригинальности и трендов.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "production", "title": "Производственный цикл", "body": "Каждая неделя включает сценарий, раскадровку, анки и чистовую. Ассистенты помогают с фоном и тонировкой, но требуют мотивации.\\n", "mechanics_links": ["mechanics/production/creative-pipeline.yaml"], "assets": []}, {"id": "deadlines", "title": "Дедлайны и стресс", "body": "Таймер дедлайна повышает стресс. Игрок решает, использовать ли энергетики или брать отпуск, рискуя потерять позицию в рейтинге.\\n", "mechanics_links": ["mechanics/wellbeing/stress-management.yaml"], "assets": []}, {"id": "publication", "title": "Публикация и отзывы", "body": "После релиза система показывает рейтинги читателей. Высокий рейтинг даёт бонус к доходам и приглашение на аниме-адаптацию.\\n", "mechanics_links": ["mechanics/economy/revenue-stream.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- Успех: 3 000 XP, пассивный доход 5 000–50 000 еддис в неделю, известность +10.\\n- Провал: 1 000 XP и возможность перезапустить серию.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест открывает ветку аниме-адаптаций, коллаборации с косплей-ивентами и медиа-контентом.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 965, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Мангака» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

