INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-spb-2020-2029-peter-paul-fortress', 'Санкт-Петербург 2020-2029 — «Петропавловская крепость»', 'Команда освобождает заключённого из корп-тюрьмы на Заячьем острове и скрывается через подземные ходы.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-spb-2020-2029-peter-paul-fortress", "title": "Санкт-Петербург 2020-2029 — «Петропавловская крепость»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint_petersburg", "quest", "jailbreak"], "topics": ["city_quests", "stealth"], "related_systems": ["narrative-service", "stealth-system", "combat-service"], "related_documents": [{"id": "github-issue-522", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/522", "relation": "migrated_to", "migrated_at": "2025-11-22T22:20:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/saint-petersburg/2020-2029/quest-003-peter-paul-fortress.md", "visibility": "internal", "audience": ["concept", "narrative", "combat"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Необходимо превратить историческую Петропавловскую крепость в современную тюрьму и задать сценарий побега.", "goal": "Дать игрокам кооперативный jailbreak с сочетанием стелса, взлома и боевых решений.", "essence": "Команда освобождает заключённого из корп-тюрьмы на Заячьем острове и скрывается через подземные ходы.", "key_points": ["Интерьеры крепости переосмыслены с усиленными системами безопасности.", "Традиция полуденного залпа пушки интегрирована как отвлекающий триггер.", "Успешный побег открывает долговременного союзника."]}, "content": {"sections": [{"id": "contract", "title": "Контракт на спасение", "body": "Игроки получают заказ на освобождение политзаключённого. Брифинг раскрывает расположение камеры и расписание патрулей.\\n", "mechanics_links": [], "assets": []}, {"id": "infiltration", "title": "Проникновение на остров", "body": "Для достижения крепости используется подводный подход или маскировка под обслуживающий персонал.\\nНева и охрана на катерах создают дополнительные риски.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_override", "title": "Взлом и освобождение", "body": "Внутри участники отключают камеры, используют полуденный выстрел пушки как отвлекающий манёвр и вскрывают камеры.\\nВозможны варианты: стелс или силовое проникновение.\\n", "mechanics_links": [], "assets": []}, {"id": "escape", "title": "Эвакуация", "body": "После освобождения необходимо вывести цель через исторические туннели.\\nФинал — бегство по ледяному или водному маршруту в зависимости от сезона.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 522, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Петропавловская крепость» в формат knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, 