INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-moscow-2061-2077-corpo-wedding', 'Москва 2061-2077 — «Корпоративная свадьба»', 'Игрок организует свадьбу, настраивает церемонию и банкет, получая подарки и рост репутации.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-moscow-2061-2077-corpo-wedding", "title": "Москва 2061-2077 — «Корпоративная свадьба»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T05:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "lore_analyst", "contact": "lore@necp.game"}], "tags": ["moscow", "social-event", "wedding"], "topics": ["prestige", "social-systems"], "related_systems": ["narrative-service", "social-system", "economy-system"], "related_documents": [{"id": "canon-region-cis-moscow-2040-2093", "relation": "contextualizes"}, {"id": "canon-quest-moscow-2061-2077-family-bonds", "relation": "connected"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/moscow/2061-2077/quest-038-corpo-wedding.md", "visibility": "internal", "audience": ["lore", "narrative", "systems"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Черновик свадебного события не включал этапы подготовки, бюджетов и социальных бонусов.", "goal": "Структурировать корпоративную свадьбу с выбором масштаба, мини-играми и статусными наградами.", "essence": "Игрок организует свадьбу, настраивает церемонию и банкет, получая подарки и рост репутации.", "key_points": ["Квест-id MOSCOW-2077-038, тип social, сложность easy, длительность 1–2 часа.", "Три бюджета влияют на трансляции и количество подарков.", "Инвайт друзей и мини-игры создают социальный геймплей."]}, "content": {"sections": [{"id": "planning", "title": "Планирование и бюджет", "body": "Игрок выбирает площадку, формирует список гостей и определяет уровень бюджета (10 000, 50 000 или 200 000 едди). Система экономики резервирует средства.\\n", "mechanics_links": [], "assets": []}, {"id": "shopping", "title": "Подготовка атрибутов", "body": "Покупка нарядов, колец и декораций. Можно заказать трансляцию церемонии и нанять ведущего, что добавляет уникальные сцены.\\n", "mechanics_links": [], "assets": []}, {"id": "ceremony", "title": "Церемония и банкет", "body": "Катсцена бракосочетания, затем банкет с мини-играми (танцы, тосты, VR-фото). Гости — NPC и приглашённые игроки.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Подарки и статус", "body": "- 1 000 XP, подарки гостей (0–100 000 едди) и рост семейного статуса.\\n- Роскошный бюджет даёт +10 репутации элиты и постоянный доступ к VIP-мероприятиям.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 275, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "lore_team", "changes": "Конверсия квеста «Корпоративная свадьба» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

