INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-spb-2020-2029-nevsky-prospekt', 'Санкт-Петербург 2020-2029 — «Невский проспект»', 'Игрок проходит от Адмиралтейства до Александро-Невской лавры, открывая архитектурные точки и атмосферные события.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-spb-2020-2029-nevsky-prospekt", "title": "Санкт-Петербург 2020-2029 — «Невский проспект»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint_petersburg", "quest", "tourism"], "topics": ["city_quests", "cultural_experience"], "related_systems": ["narrative-service", "social-system"], "related_documents": [{"id": "github-issue-523", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/523", "relation": "migrated_to", "migrated_at": "2025-11-22T22:20:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/saint-petersburg/2020-2029/quest-004-nevsky-prospekt.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужно подчеркнуть культурное ядро Петербурга и показать Невский проспект как обязательный туристический маршрут.", "goal": "Подготовить прогулочный квест с историческими остановками, социальными взаимодействиями и лёгким культурным прогрессом.", "essence": "Игрок проходит от Адмиралтейства до Александро-Невской лавры, открывая архитектурные точки и атмосферные события.", "key_points": ["Маршрут делится на пять ключевых остановок с уникальными описаниями.", "Квест усиливает социальные и культурные параметры персонажа.", "Событие работает как входная точка в петербургскую арку и романтическую линию."]}, "content": {"sections": [{"id": "start_admiralty", "title": "Адмиралтейство", "body": "Прогулка начинается у шпиля с корабликом — символа города.\\nИгрок фиксирует старт, получает краткую лекцию гида и настраивает AR-навигатор.\\n", "mechanics_links": [], "assets": []}, {"id": "kazan_cathedral", "title": "Казанский собор", "body": "У колоннады запускается сценка с уличными музыкантами.\\nМожно сделать пожертвование, чтобы получить благословение и временный бафф.\\n", "mechanics_links": [], "assets": []}, {"id": "singer_house", "title": "Дом Зингера", "body": "В здании с глобусом на крыше доступен книжный и AR-галерея ретро-рекламы.\\nИгрок собирает коллекционные открытки.\\n", "mechanics_links": [], "assets": []}, {"id": "liteyny_crossroad", "title": "Перекрёсток с Литейным", "body": "Встреча с разношёрстными НПЦ — художниками, блогерами и туристами.\\nВыбор диалогов влияет на культурный счётчик.\\n", "mechanics_links": [], "assets": []}, {"id": "finish_lavra", "title": "Александро-Невская лавра", "body": "Финальная остановка завершается короткой церемонией благодарности.\\nИгрок получает достижение «Прогулка по Невскому» и открывает новые экскурсионные цепочки.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 523, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Невский проспект» в формат knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, l