INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-spb-2020-2029-dostoevsky-walk', 'Санкт-Петербург 2020-2029 — «Прогулка Достоевского»', 'Игрок повторяет путь героя романа, взаимодействует с AR-сценами и открывает скрытые истории города.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-spb-2020-2029-dostoevsky-walk", "title": "Санкт-Петербург 2020-2029 — «Прогулка Достоевского»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint_petersburg", "quest", "literature"], "topics": ["city_quests", "cultural_experience"], "related_systems": ["narrative-service", "social-system"], "related_documents": [{"id": "github-issue-524", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/524", "relation": "migrated_to", "migrated_at": "2025-11-22T22:20:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/saint-petersburg/2020-2029/quest-007-dostoevsky-walk.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Литературное наследие Петербурга требует иммерсивного опыта, погружающего игрока в атмосферу «Преступления и наказания».", "goal": "Организовать прогулочный квест по маршруту Раскольникова с эмоциональными триггерами и культурными бонусами.", "essence": "Игрок повторяет путь героя романа, взаимодействует с AR-сценами и открывает скрытые истории города.", "key_points": ["Узкие дворы, каналы и мосты создают характерный визуальный стиль.", "Квест усиливает культурные параметра и добавляет достижения читателя Достоевского.", "Используются AR-фрагменты текста и аудиовставки для усиления атмосферы."]}, "content": {"sections": [{"id": "starting_point", "title": "Дом Раскольникова", "body": "Игрок получает гида в AR и знакомится с биографией персонажей.\\nНачальная сцена задаёт мрачный тон петербургских двориков.\\n", "mechanics_links": [], "assets": []}, {"id": "key_locations", "title": "Места романа", "body": "Маршрут включает дом процентщицы, Сенную площадь, мосты и полицейскую контору.\\nНа каждой точке активируются текстовые и аудио воспоминания.\\n", "mechanics_links": [], "assets": []}, {"id": "reflection", "title": "Внутренний монолог", "body": "Игроку предлагается выбрать мысли, отражающие моральное состояние героя.\\nВыбор влияет на записи в персональном дневнике.\\n", "mechanics_links": [], "assets": []}, {"id": "conclusion", "title": "Подведение итогов", "body": "Завершение прогулки открывает коллекционные материалы о Достоевском и даёт культурные бонусы.\\nДополнительно можно открыть новую ветку романсов.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 524, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Прогулка Достоевского» в формат knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURREN