INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-moscow-2061-2077-implant-addiction', 'Москва 2061-2077 — «Имплантная зависимость»', 'Врач диагностирует начальную стадию психоза, а игрок выбирает лечение, балансируя деньги, время и потери имплантов.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-moscow-2061-2077-implant-addiction", "title": "Москва 2061-2077 — «Имплантная зависимость»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T05:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "lore_analyst", "contact": "lore@necp.game"}], "tags": ["moscow", "implants", "cyberpsychosis"], "topics": ["health-care", "psychological-drama"], "related_systems": ["narrative-service", "health-system", "implant-system"], "related_documents": [{"id": "canon-region-cis-moscow-2040-2093", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/moscow/2061-2077/quest-035-implant-addiction.md", "visibility": "internal", "audience": ["lore", "narrative", "systems"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Черновик описывал зависимость от имплантов без этапов лечения и связей с системами здоровья и психоза.", "goal": "Структурировать социальный квест о диагностике кибер-психоза и выборе терапий.", "essence": "Врач диагностирует начальную стадию психоза, а игрок выбирает лечение, балансируя деньги, время и потери имплантов.", "key_points": ["Квест-id MOSCOW-2077-035, тип social, сложность medium, длительность 2–4 часа.", {"Четыре подхода к лечению": "терапия, удаление имплантов, эксперимент или игнорирование."}, "Последствия отражают здоровье, финансы и риск берсерка."]}, "content": {"sections": [{"id": "diagnosis", "title": "Диагноз и симптомы", "body": "Игрок обращается к врачу из клиники Архологии. Скрипты здоровья фиксируют галлюцинации и агрессию, активируются дебаффы реакции и точности.\\n", "mechanics_links": [], "assets": []}, {"id": "options", "title": "Варианты лечения", "body": "Врач предлагает четыре метода: длительная терапия, удаление имплантов, экспериментальное вмешательство или отказ. Каждый вариант связан с разными сценами и затратами.\\n", "mechanics_links": [], "assets": []}, {"id": "consequences", "title": "Последствия решения", "body": "- Терапия снимает симптомы через десять сеансов и стоит 10 000 едди.\\n- Удаление имплантов снижает параметры силы, но очищает психику.\\n- Эксперимент даёт шанс сохранить импланты, однако повышает шанс рецидива.\\n- Игнорирование усиливает психоз, активирует случайные берсерк-события.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и связь с системами", "body": "- 1 500 XP и исцеление психоза при успешном лечении.\\n- Обновление статусов в системе имплантов и предупреждение в журнале здоровья.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 275, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "lore_team", "changes": "Конверсия квеста «Имплантная зависимость» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_