INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-2040-2060-taiko-tournament', 'Токио 2040-2060 — «Турнир тайко»', 'Игрок выступает на сцене мацури, отстукивая ритм на гигантских тайко ради титула «Мастер тайко».', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-2040-2060-taiko-tournament", "title": "Токио 2040-2060 — «Турнир тайко»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-23T01:55:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["tokyo", "quest", "taiko"], "topics": ["rhythm-games", "festival-culture"], "related_systems": ["narrative-service", "rhythm-service", "liveops-events-service"], "related_documents": [{"id": "github-issue-974", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/974", "relation": "migrated_to", "migrated_at": "2025-11-23T01:55:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/tokyo/2040-2060/quest-025-taiko-tournament.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Музыкальная линия Токио не показывала ритм-турниры с барабанами тайко.", "goal": "Добавить соревновательный квест с живыми выступлениями, мини-игрой и фестивальной атмосферой.", "essence": "Игрок выступает на сцене мацури, отстукивая ритм на гигантских тайко ради титула «Мастер тайко».", "key_points": ["Ритм-мини-игра строится на последовательностях традиционных ударов.", "Турнир поддерживает фестивальные LiveOps и музыкальные коллаборации.", "Неоново-традиционный сетап подчёркивает смесь будущего и наследия."]}, "content": {"sections": [{"id": "registration", "title": "Регистрация и выбор трека", "body": "Игрок оплачивает взнос, выбирает трек (традиционный или J-Rock ремикс) и получает тренировочный доступ к барабанам.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "performance", "title": "Стадии турнира", "body": "Серия раундов с возрастающей сложностью ритмов. Мини-игра измеряет точность, тайминг и динамику ударов.\\n", "mechanics_links": ["mechanics/rhythm/performance.yaml"], "assets": []}, {"id": "finale", "title": "Финальная баталия", "body": "Финал проходит на главной сцене мацури с синхронным выступлением. Игрок соревнуется с фаворитом, используя специальные комбо.\\n", "mechanics_links": ["mechanics/competition/bracket-tournament.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- Победа: 20 000 еддис, титул «Мастер тайко», эксклюзивные барабанные палочки.\\n- Участие: 1 000 XP и повышение репутации среди музыкантов.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связан с музыкальными аренами, караоке-турнирами и фестивалями регионов Канто.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 974, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Турнир тайко» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements