-- Quest: canon-quest-spb-2030-2039-tsarskoye-selo
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-spb-2030-2039-tsarskoye-selo', 'Санкт-Петербург 2030-2039 — «Царское Село»', 'Игрок посещает императорскую резиденцию, воссозданную Янтарную комнату и парки, углубляя связь с историей Романовых.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-spb-2030-2039-tsarskoye-selo", "title": "Санкт-Петербург 2030-2039 — «Царское Село»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint_petersburg", "quest", "palace"], "topics": ["city_quests", "cultural-tour"], "related_systems": ["narrative-service", "social-service"], "related_documents": [{"id": "github-issue-525", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/525", "relation": "migrated_to", "migrated_at": "2025-11-22T22:30:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/saint-petersburg/2030-2039/quest-013-tsarskoye-selo.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Культурный маршрут по Царскому Селу не был структурирован в базе знаний и не подключён к дворцовой линии.", "goal": "Сформировать туристический квест, соединяющий Екатерининский и Александровский дворцы, Лицей и парки.", "essence": "Игрок посещает императорскую резиденцию, воссозданную Янтарную комнату и парки, углубляя связь с историей Романовых.", "key_points": ["Погружение в архитектуру барокко и императорские залы.", "Образовательные элементы о Лицее и Пушкине.", "Подготовка к сюжетам Янтарной комнаты и другим дворцовым квестам."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Атмосферный культурный квест: игрок едет в Царское Село, посещает дворцы, парки и музей Лицея, наблюдая\\nвоссозданную Янтарную комнату и размышляя о судьбе империи.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Прибыть в Царское Село (25 км от Санкт-Петербурга).\\n2. Исследовать Екатерининский дворец и парадные залы.\\n3. Осмотреть воссозданную Янтарную комнату.\\n4. Пройти экскурсию по Лицею и паркам.\\n5. Встретиться с историками или экскурсоводами для обсуждения наследия.\\n", "mechanics_links": ["mechanics/world/events/live-events-system.yaml"], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "- Бирюзово-золотой фасад и золотая анфилада Екатерининского дворца.\\n- Парк с павильонами, прудами и мраморными скульптурами.\\n- Александровский дворец и история Николая II.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- XP 1 500, Валюта -100 (стоимость билетов), прирост культуры +10.\\n- Ачивка «Императорский визит».\\n- Повышение лояльности культурных фракций и доступ к элитным событиям.\\n", "mechanics_links": ["mechanics/progression/progression-attributes-matrix.yaml"], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Туристический маршрут с образовательными элементами.\\n- Диалоговые сцены и фотомеханики для коллекционеров.\\n- Связь с дворцовыми сюжетами и культурными коллекциями.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связан с «Янтарной комнатой» и другими культурными событиями Петербурга, открывает новые маршруты и\\nвозможные миссии в пригородах.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 525, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Царское Село» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

