INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-2040-2060-boso-tribe', 'Токио 2040-2060 — «Племя босодзоку»', 'Игрок выполняет испытание на эстакаде, модернизирует байк и становится членом босодзоку, принимая риск постоянного розыска.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-2040-2060-boso-tribe", "title": "Токио 2040-2060 — «Племя босодзоку»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-23T01:50:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["tokyo", "quest", "bosozoku"], "topics": ["street-culture", "faction-gameplay"], "related_systems": ["narrative-service", "vehicle-service", "law-enforcement-service"], "related_documents": [{"id": "github-issue-976", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/976", "relation": "migrated_to", "migrated_at": "2025-11-23T01:50:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/tokyo/2040-2060/quest-027-boso-tribe.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Городская линия Токио не раскрывала байкерскую субкультуру босодзоку с её рисками и кастомизацией.", "goal": "Добавить групповой квест вступления в племя с гонкой, тюнингом байка и противостоянием полиции.", "essence": "Игрок выполняет испытание на эстакаде, модернизирует байк и становится членом босодзоку, принимая риск постоянного розыска.", "key_points": ["Гонки и ночные рейды дают ощущение анархии и скорости.", "Кастомизация байка влияет на геймплей и визуальную идентичность.", "Полицейский преследователь создаёт постоянное напряжение."]}, "content": {"sections": [{"id": "initiation", "title": "Знакомство с племенем", "body": "Игрок встречается с лидером на парковке, соглашается на испытание и узнаёт кодекс банды.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "race", "title": "Гонка по эстакаде", "body": "Ночная гонка требует уклоняться от трафика и патрулей. Победа открывает доступ к кастомизации.\\n", "mechanics_links": ["mechanics/vehicle/high-speed-race.yaml"], "assets": []}, {"id": "customization", "title": "Тюнинг байка", "body": "Игрок модифицирует байк: высокие рули, флаги, громкий выхлоп. Параметры влияют на скорость, устойчивость и заметность.\\n", "mechanics_links": ["mechanics/customization/vehicle-mods.yaml"], "assets": []}, {"id": "raid", "title": "Ночной рейд", "body": "Группа выезжает в центр города, устраивает шоу громких двигателей. Полиция начинает преследование, требуется командная координация.\\n", "mechanics_links": ["mechanics/faction/group-operations.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 2 000 XP, доступ к базе босодзоку, уникальный байк.\\n- Розыск полиции повышается до уровня «наблюдение».\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связывает байкерскую культуру с криминальными сюжетами, гонками Сибуи и глобальными сетками стрит-гангов.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 976, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Племя босодзоку» в формат знаний."}], "validation": {"checksum": "", "schema_