-- Quest: canon-quest-spb-2030-2039-cultural-capital
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-spb-2030-2039-cultural-capital', 'Санкт-Петербург 2030-2039 — «Культурная столица»', 'Игрок проходит марафон по иконкам Петербурга за один день, укрепляя репутацию города как культурной столицы.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-spb-2030-2039-cultural-capital", "title": "Санкт-Петербург 2030-2039 — «Культурная столица»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint_petersburg", "quest", "culture-marathon"], "topics": ["city_quests", "cultural-tour"], "related_systems": ["narrative-service", "social-service"], "related_documents": [{"id": "github-issue-526", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/526", "relation": "migrated_to", "migrated_at": "2025-11-22T22:30:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/saint-petersburg/2030-2039/quest-014-cultural-capital.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Культурный марафон по десяти объектам Петербурга не имел структурированного описания и тайминга.", "goal": "Сформировать маршрут «Культурная столица», объединяющий музеи, соборы и театры за одно игровое событие.", "essence": "Игрок проходит марафон по иконкам Петербурга за один день, укрепляя репутацию города как культурной столицы.", "key_points": ["Маршрут по десяти ключевым точкам: Эрмитаж, Мариинка, Кунсткамера и др.", "Тайм-челлендж с ограничением по времени и требованиями к знаниям.", "Развитие культурной репутации и связь с другими квестами города."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Игрок принимает вызов «Культурного марафона» и посещает десять знаковых объектов Петербурга за один день, собирая\\nвпечатления, артефакты и отзывы о городе-музее.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "route", "title": "Маршрут", "body": "1. Эрмитаж — крупнейший музей России.\\n2. Исаакиевский собор — золотой купол и обзорная площадка.\\n3. Спас на Крови — мозаичный собор.\\n4. Русский музей — русское искусство.\\n5. Мариинский театр — опера и балет.\\n6. Петропавловская крепость — история основания города.\\n7. Казанский собор — колоннада на Невском.\\n8. Михайловский замок — трагедия Павла I.\\n9. Кунсткамера — первый музей России.\\n10. Стрелка Васильевского острова — панорама города.\\n", "mechanics_links": ["mechanics/world/events/live-events-system.yaml"], "assets": []}, {"id": "challenges", "title": "Испытания", "body": "- Выполнить маршрут за ограниченное время.\\n- Выполнить образовательные мини-квесты и фото-челленджи на каждой локации.\\n- Координировать перемещение между объектами в оживлённом туристическом центре.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- XP 5 000, Валюта -500 (билеты), культура +25.\\n- Титул «Знаток Петербурга» и доступ к VIP-мероприятиям.\\n- Повышенный интерес туристов и новые культурные события.\\n", "mechanics_links": ["mechanics/progression/progression-attributes-matrix.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связывает основные культурные объекты с другими сценариями Петербурга и расширяет доступ к культурным\\nсюжетам, включая специальные ночные экскурсии.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 526, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Культурная столица» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

