INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-spb-2030-2039-pushkin-duel', 'Санкт-Петербург 2030-2039 — «Дуэль Пушкина»', 'Игрок участвует в VR-реконструкции дуэли на Чёрной речке, выбирая между историей, альтернативой и самопожертвованием.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-spb-2030-2039-pushkin-duel", "title": "Санкт-Петербург 2030-2039 — «Дуэль Пушкина»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint_petersburg", "quest", "vr-simulation"], "topics": ["city_quests", "historical-mystery"], "related_systems": ["narrative-service", "social-service"], "related_documents": [{"id": "github-issue-526", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/526", "relation": "migrated_to", "migrated_at": "2025-11-22T22:30:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/saint-petersburg/2030-2039/quest-015-pushkin-duel.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Симуляция дуэли Пушкина существовала только в Markdown и не была интегрирована в культурную линию.", "goal": "Структурировать VR-квест, позволяющий игроку пережить последний день поэта и повлиять на исход.", "essence": "Игрок участвует в VR-реконструкции дуэли на Чёрной речке, выбирая между историей, альтернативой и самопожертвованием.", "key_points": ["VR-симуляция с эмоциональными сценами последнего дня Пушкина.", "Вариативность исхода: сохранить историю, переписать её или принять удар на себя.", "Усиление литературной линии Петербурга за счёт культурного контента."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Игрок подключается к VR-постановке, где проживает последние часы Пушкина и решает, вмешиваться ли в ход истории.\\nКвест сочетает интерактивные диалоги, переговоры и дуэльную сцену.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Войти в симуляцию и изучить окружение.\\n2. Провести переговоры с секундантами и Дантесом, пытаясь отменить дуэль.\\n3. Приготовиться к дуэли при неудаче дипломатии.\\n4. Принять решение во время выстрела.\\n5. Получить одну из альтернативных концовок и реакцию культурной хроники.\\n", "mechanics_links": ["mechanics/world/events/live-events-system.yaml"], "assets": []}, {"id": "choices", "title": "Выборы", "body": "- Предотвратить конфликт и сохранить жизнь поэта.\\n- Не вмешиваться и оставить историю неизменной.\\n- Заменить Пушкина и погибнуть ради легенды.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "- Снежное поле у Чёрной речки и исторические декорации.\\n- Реконструированные пистолеты дуэлянтов и одежда XIX века.\\n- Цитаты из стихов, сопровождающие ключевые сцены.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- XP 2 500, культура +15, материальные награды отсутствуют.\\n- Эмоциональное событие, влияющее на литературную линию и отношение культурных фракций.\\n", "mechanics_links": ["mechanics/progression/progression-attributes-matrix.yaml"], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- VR-интерактив с проживанием исторических событий.\\n- Дуэльная мини-игра и QTE-последовательности.\\n- Альтернативная история, влияющая на хронику города.\\n", "mechanics_links": ["mechanics/combat/combat-pvp-pve.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест дополняет литературные маршруты Петербурга и может влиять на доступ к музеям и культурным событиям в\\nЦарском Селе и Лицее.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 526, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Дуэль Пушкина» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

