INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-2040-2060-harajuku-fashion', 'Токио 2040-2060 — «Мода Харадзюку»', 'Игрок собирает уникальный образ, участвует в показе и становится иконой уличной моды.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-2040-2060-harajuku-fashion", "title": "Токио 2040-2060 — «Мода Харадзюку»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["tokyo", "quest", "fashion"], "topics": ["street-fashion", "influencer-culture"], "related_systems": ["narrative-service", "customization-service", "reputation-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/tokyo/2040-2060/quest-029-harajuku-fashion.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Модная линия Токио нуждалась в квесте о трендсеттинге Харадзюку и влиянии на соцсети.", "goal": "Создать стильный квест с кастомизацией, фотосессиями и модным шоу на Takeshita Street.", "essence": "Игрок собирает уникальный образ, участвует в показе и становится иконой уличной моды.", "key_points": ["Глубокая кастомизация образа влияет на фейм и стиль.", "Фотосессии и соцсети усиливают механики влияния.", "Модное шоу подключает LiveOps-ивенты брендов."]}, "content": {"sections": [{"id": "shopping", "title": "Шопинг на Takeshita Street", "body": "Игрок исследует бутики, собирает элементы образа из стилей лолита, декора и готик. Бюджет и переговоры влияют на доступ к редким\\nпредметам.\\n", "mechanics_links": ["mechanics/economy/retail-shopping.yaml"], "assets": []}, {"id": "styling", "title": "Сборка образа", "body": "Система кастомизации позволяет комбинировать одежду, аксессуары и макияж. Игрок оценивает гармонию и оригинальность.\\n", "mechanics_links": ["mechanics/customization/outfit-builder.yaml"], "assets": []}, {"id": "photoshoot", "title": "Уличная фотосессия", "body": "NPC-фотографы делают снимки для соцсетей. Игрок выполняет позы и получает фейм-пойнты в зависимости от реакции аудитории.\\n", "mechanics_links": ["mechanics/media/social-influence.yaml"], "assets": []}, {"id": "fashion_show", "title": "Модное шоу", "body": "Финал проходит на подиуме. Судьи оценивают оригинальность, соответствие трендам и харизму. Возможны коллаборации с брендами.\\n", "mechanics_links": ["mechanics/performance/runway-show.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 800 XP, фейм +10, стиль +15, расходы 5 000 еддис.\\n- Титул «Икона Харадзюку» и доступ к модным контрактам.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связан с косплей-чемпионатом, караоке-сценой и рекламными кампаниями азиатских брендов.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 929, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Мода Харадзюку» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

