-- Quest: canon-quest-spb-2030-2039-summer-garden
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-spb-2030-2039-summer-garden', 'Санкт-Петербург 2030-2039 — «Летний сад»', 'Игроки проводят вечер в Летнем саду, исследуя статуи и павильоны, усиливая романтические и культурные параметры.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-spb-2030-2039-summer-garden", "title": "Санкт-Петербург 2030-2039 — «Летний сад»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint_petersburg", "quest", "romance"], "topics": ["city_quests", "cultural-tour"], "related_systems": ["narrative-service", "social-service"], "related_documents": [{"id": "github-issue-526", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/526", "relation": "migrated_to", "migrated_at": "2025-11-22T22:30:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/saint-petersburg/2030-2039/quest-016-summer-garden.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Романтическая прогулка по Летнему саду не описана в knowledge-entry и не использует потенциал культурных бонусов.", "goal": "Структурировать квест свидания в историческом парке с акцентом на атмосферу и взаимодействие с партнёром.", "essence": "Игроки проводят вечер в Летнем саду, исследуя статуи и павильоны, усиливая романтические и культурные параметры.", "key_points": ["Иммерсивная прогулка по первому парку Петербурга с мраморными статуями.", "Квест усиливает романтические отношения и культурные показатели.", "Подходит для спокойной передышки между масштабными сюжетами."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Атмосферное свидание в Летнем саду: игроки входят через знаменитую чугунную ограду, бродят по аллеям и\\nизучают античные статуи, погружаясь в тишину исторического Санкт-Петербурга.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Войти в сад и настроиться на неспешную прогулку.\\n2. Исследовать аллеи, рассматривая статуи и считывая их истории в AR.\\n3. Посетить Летний дворец Петра I и узнать о первых годах города.\\n4. Наблюдать за лебедями на прудах, обсуждая прошлое и будущее Петербурга.\\n5. Завершить свидание у фонтана, усилив связь с партнёром.\\n", "mechanics_links": ["mechanics/world/events/live-events-system.yaml"], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "- Чугунная ограда с золотыми копьями.\\n- Мраморные скульптуры античных богов и героев.\\n- Летний дворец и тишина императорского сада.\\n- Лебеди и подсветка вечерних аллей.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- XP 600, романтический бонус +10, персональная фотография в альбоме.\\n- Ачивка «Прогулка Императора».\\n- Улучшение отношений с партнёром или NPC-компаньоном.\\n", "mechanics_links": ["mechanics/progression/progression-attributes-matrix.yaml"], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Романтические диалоговые сцены и выбор настроения.\\n- Сканирование статуй и сбор культурных фактов.\\n- Фото-режим с фильтрами эпохи Петра I.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест поддерживает романтические и дворцовые линии Петербурга, дополняет сценарии Царского Села и сезонных\\nмероприятий в садах города.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 526, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Летний сад» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

