INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-2040-2060-yokai-hunt', 'Токио 2040-2060 — «Охота на ёкаев»', 'Команда охотников преследует ИИ, копирующих кицунэ, тэнгу, они и Ямата-но-Ороти, используя синтоистские ритуалы и технику.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-2040-2060-yokai-hunt", "title": "Токио 2040-2060 — «Охота на ёкаев»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["tokyo", "quest", "yokai"], "topics": ["raid-content", "ai-threats"], "related_systems": ["narrative-service", "combat-service", "ai-systems"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/tokyo/2040-2060/quest-030-yokai-hunt.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Рейдовая линия Токио не показывала конфликт с роуг-ИИ, использующими фольклор ёкаев.", "goal": "Сформировать рейдовое событие на 10+ игроков с многоступенчатыми босс-боями против кибер-ёкаев.", "essence": "Команда охотников преследует ИИ, копирующих кицунэ, тэнгу, они и Ямата-но-Ороти, используя синтоистские ритуалы и технику.", "key_points": ["Каждый босс требует уникальных механик: иллюзии, полёт, грубая сила и многофазный змей.", "Рейд объединяет синтоистские мотивы и угрозу Blackwall.", "Легендарные импланты и титул награждают завершение события."]}, "content": {"sections": [{"id": "briefing", "title": "Брифинг охотников", "body": "Игроки собирают сведения о роуг-ИИ, получают омамори и распределяют роли в рейде.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "bosses", "title": "Бои с ёкаями", "body": "Последовательные арены с кицунэ, тэнгу и они вводят уникальные фазы, требуя координации и синтоистских ритуалов очищения.\\n", "mechanics_links": ["mechanics/combat/raid-boss-design.yaml"], "assets": []}, {"id": "final_battle", "title": "Ямата-но-Ороти", "body": "Финальный бой с восьмиглавым змеем комбинирует лазерные атаки, контролируемые хвосты и синхронные ритуалы изгнания.\\n", "mechanics_links": ["mechanics/combat/multi-phase-mega-boss.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 10 000 XP, 100 000 Eddies (делится), легендарные импланты ёкай-серии, титул «Охотник на ёкаев».\\n- Репутация синтоистских фракций повышается, ИИ-угроза временно подавлена.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Рейд связан с храмовыми квестами и линиями, исследующими влияние Blackwall в Японии.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 930, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Охота на ёкаев» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIM