INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-spb-2020-2029-hermitage-heist', 'Санкт-Петербург 2020-2029 — «Ограбление Эрмитажа»', 'Команда игроков проникает в Эрмитаж через Зимний дворец, крадёт шедевр и скрывается по Неве, сталкиваясь с международным розыском.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-spb-2020-2029-hermitage-heist", "title": "Санкт-Петербург 2020-2029 — «Ограбление Эрмитажа»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint_petersburg", "quest", "heist"], "topics": ["city_quests", "stealth"], "related_systems": ["narrative-service", "stealth-system", "economy-service"], "related_documents": [{"id": "github-issue-522", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/522", "relation": "migrated_to", "migrated_at": "2025-11-22T22:20:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/saint-petersburg/2020-2029/quest-001-hermitage-heist.md", "visibility": "internal", "audience": ["concept", "narrative", "economy"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужно представить масштабное музейное ограбление и выделить культурную значимость Эрмитажа в рамках криминальной линии Петербурга.", "goal": "Организовать многоэтапный стелс-квест с выбором целевого экспоната, уникальными маршрутами и высокими ставками.", "essence": "Команда игроков проникает в Эрмитаж через Зимний дворец, крадёт шедевр и скрывается по Неве, сталкиваясь с международным розыском.", "key_points": ["Игроки исследуют легендарные залы музея и выбирают символичную добычу.", "Миссия сочетает подготовку, взлом, обход лазеров и кооперативную эвакуацию.", "Последствия ограбления запускают глобальные репутационные и сюжетные ответки."]}, "content": {"sections": [{"id": "casing", "title": "Сбор информации", "body": "Перед ограблением команда выполняет разведку: изучает расписание охраны, карту лазерных сеток и патруль дронов.\\nИгроки анализируют экспозиции, выбирают цель и готовят экипировку.\\n", "mechanics_links": [], "assets": []}, {"id": "infiltration", "title": "Проникновение", "body": "В ходе ночной операции необходимо проникнуть в Зимний дворец, избегая камер и физической охраны.\\nИгроки используют скрытые проходы, вентиляционные шахты и социальную инженерию.\\n", "mechanics_links": [], "assets": []}, {"id": "art_extraction", "title": "Кража экспоната", "body": "В залах с малахитовой колоннадой и парадной лестницей требуется синхронно отключить лазеры и заменить артефакт муляжом.\\nВыбор экспоната (Рембрандт, Да Винчи или корона Романовых) влияет на сложность и ценность награды.\\n", "mechanics_links": [], "assets": []}, {"id": "escape", "title": "Эвакуация", "body": "После взятия трофея начинается тревога. Команда покидает Эрмитаж через служебные коридоры и канализационные ходы.\\nФинальный участок — побег по Неве на катере с преследованием Федералов.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 522, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Ограбление Эрмитажа» в формат knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.descript