INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-2061-2077-cyber-shogun', 'Токио 2061-2077 — «Кибер-сёгун»', 'Игроки собирают делегации кланов, ведут переговоры с ИИ и выбирают: служить, свергнуть или создать совет даймё.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-2061-2077-cyber-shogun", "title": "Токио 2061-2077 — «Кибер-сёгун»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["tokyo", "quest", "shogunate"], "topics": ["political-conflict", "raid-content"], "related_systems": ["narrative-service", "combat-service", "governance-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/tokyo/2061-2077/quest-031-cyber-shogun.md", "visibility": "restricted", "audience": ["concept", "narrative", "liveops"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Политическая арка Токио требовала масштабного события с ИИ, претендующим на власть над кланами.", "goal": "Создать рейдовый сюжет, где игроки решают судьбу «Кибер-сёгуна», формируя новый порядок кланов.", "essence": "Игроки собирают делегации кланов, ведут переговоры с ИИ и выбирают: служить, свергнуть или создать совет даймё.", "key_points": ["Рейд объединяет до десяти представителей кланов с уникальными целями.", "Решение влияет на власть в Токио и состояние криминальной сцены.", "Исход фиксируется в LiveOps, меняя доступные контракты."]}, "content": {"sections": [{"id": "convene", "title": "Созыв кланов", "body": "Игроки привлекают якудза-фракции, убеждают боссов и формируют делегацию для встречи с ИИ-сёгуном.\\n", "mechanics_links": ["mechanics/factions/faction-alignment.yaml"], "assets": []}, {"id": "negotiation", "title": "Переговоры и испытания", "body": "Кибер-сёгун тестирует лояльность и силу. Игроки принимают решения, влияющие на доверие ИИ и готовность к финальному выбору.\\n", "mechanics_links": ["mechanics/narrative/interactive-council.yaml"], "assets": []}, {"id": "resolution", "title": "Исход сёгуната", "body": "Три ветки определяют будущее: служение ИИ, свержение с войной кланов или создание совета даймё с коллективным управлением.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 10 000 XP, до 200 000 Eddies, титул «Даймё» при союзном исходе.\\n- Глобальная реакция кланов, изменение доступности контрактов и охоты на мятежных лидеров.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Событие связано с линиями якудза, самурайских дуэлей и международными сюжетами по восстановлению монархий.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 931, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Кибер-сёгун» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_