-- Quest: canon-quest-spb-2030-2039-rostral-columns
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-spb-2030-2039-rostral-columns', 'Санкт-Петербург 2030-2039 — «Ростральные колонны»', 'Игрок поднимается на колонну, зажигает огонь и запускает праздник на Стрелке Васильевского острова.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-spb-2030-2039-rostral-columns", "title": "Санкт-Петербург 2030-2039 — «Ростральные колонны»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint_petersburg", "quest", "ceremony"], "topics": ["city_quests", "civic-events"], "related_systems": ["narrative-service", "liveops-service"], "related_documents": [{"id": "github-issue-527", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/527", "relation": "migrated_to", "migrated_at": "2025-11-22T22:30:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/saint-petersburg/2030-2039/quest-019-rostral-columns.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Церемониальное зажжение Ростральных колонн не было описано в базе знаний и не имело игровой структуры.", "goal": "Сформировать сценарий городского праздника с участием игрока и символическим зажжением огня.", "essence": "Игрок поднимается на колонну, зажигает огонь и запускает праздник на Стрелке Васильевского острова.", "key_points": ["Подъём на 32-метровую колонну и подготовка факела.", "Городское событие с панорамой Невы и салютом.", "Праздничные бонусы для городских фракций и туристов."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Игрок получает почётное поручение от городских властей: зажечь огонь на Ростральных колоннах перед началом\\nпраздника. Квест фокусируется на церемонии и городской атмосфере.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Принять задание и пройти инструктаж по технике безопасности.\\n2. Подняться на колонну при помощи подъёмника или лестницы.\\n3. Зажечь огонь в чаше и синхронизировать запуск салюта.\\n4. Наблюдать панораму города и фейерверк.\\n5. Спуститься и принять благодарность от горожан.\\n", "mechanics_links": ["mechanics/world/events/live-events-system.yaml"], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "- Красные колонны на Стрелке Васильевского острова, украшенные рострами кораблей.\\n- Зажжённые чаши огня и праздничные баннеры.\\n- Вид на Неву, Петропавловскую крепость и Эрмитаж.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- XP 1 200, 5 000 eddies, репутация города +5.\\n- Ачивка «Хранитель огня».\\n- Повышенный туристический интерес на время праздника.\\n", "mechanics_links": ["mechanics/progression/progression-attributes-matrix.yaml"], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Церемониальная мини-игра с точным таймингом.\\n- Атмосферное событие без боевых элементов.\\n- Влияние на общественный настрой и событийный календарь.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связывает праздничную линию Петербурга с другими городскими событиями, активируя дополнительные концерты\\nи фейерверки на Неве.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 527, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Ростральные колонны» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

