INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-2061-2077-bullet-train-heist', 'Токио 2061-2077 — «Ограбление синкансена»', 'Команда проникает на поезд Токио–Осака, прорывается через охрану, взламывает грузовой вагон и покидает поезд на ховербайках.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-2061-2077-bullet-train-heist", "title": "Токио 2061-2077 — «Ограбление синкансена»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["tokyo", "quest", "shinkansen"], "topics": ["mobile-heist", "transport-system"], "related_systems": ["narrative-service", "mobility-service", "stealth-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/tokyo/2061-2077/quest-033-bullet-train-heist.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Транспортная линия Токио нуждалась в динамичном хейсте на движущемся синкансене.", "goal": "Сконструировать миссию с планированием маршрута, продвижением по вагонам и высокоскоростной эвакуацией.", "essence": "Команда проникает на поезд Токио–Осака, прорывается через охрану, взламывает грузовой вагон и покидает поезд на ховербайках.", "key_points": ["Игровая площадка постоянно движется со скоростью 500 км/ч.", "Игроки балансируют между стелсом и боевыми столкновениями.", "Финальная эвакуация требует точного тайминга."]}, "content": {"sections": [{"id": "planning", "title": "Подготовка ограбления", "body": "Игроки исследуют расписание, получают доступ к маршрутным данным и выбирают точку посадки.\\n", "mechanics_links": ["mechanics/heists/heist-preparation.yaml"], "assets": []}, {"id": "boarding", "title": "Посадка и продвижение", "body": "Проникновение на поезд и бой с охраной. Нужно перемещаться по вагонам, избегая камер и законных пассажиров.\\n", "mechanics_links": ["mechanics/stealth/multi-path-infiltration.yaml"], "assets": []}, {"id": "cargo", "title": "Взлом грузового вагона", "body": "Взлом систем безопасности, извлечение контейнера с секретами и подготовка к выходу.\\n", "mechanics_links": ["mechanics/hacking/mobile-terminal-breach.yaml"], "assets": []}, {"id": "escape", "title": "Эвакуация на скорости", "body": "Игроки синхронизируют прыжок с поезда на ховербайки, уходят от погони и скрываются.\\n", "mechanics_links": ["mechanics/mobility/high-speed-escape.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 3 000 XP, 50 000 Eddies, корп-секреты и ачивка «Пуля в пути».\\n- Рост репутации среди криминальных сетей и риск преследования корпорации.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест интегрирован в транспортные события Японии и связан с дальнейшими хейстами на движущихся объектах.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 933, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Ограбление синкансена» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCL