-- Quest: canon-quest-spb-2030-2039-imperial-ball
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-spb-2030-2039-imperial-ball', 'Санкт-Петербург 2030-2039 — «Императорский бал»', 'Игроки готовят наряд, осваивают танцы и участвуют в светском вечере среди петербургской элиты.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-spb-2030-2039-imperial-ball", "title": "Санкт-Петербург 2030-2039 — «Императорский бал»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint_petersburg", "quest", "high_society"], "topics": ["city_quests", "social-events"], "related_systems": ["narrative-service", "social-service"], "related_documents": [{"id": "github-issue-527", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/527", "relation": "migrated_to", "migrated_at": "2025-11-22T22:30:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/saint-petersburg/2030-2039/quest-020-imperial-ball.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Императорский бал в Зимнем дворце задуман как элитное событие, но пока описан лишь в Markdown и не интегрирован в систему социальных квестов.", "goal": "Структурировать элитный бал с этапами подготовки, этикета и кульминации, чтобы задействовать репутацию высшего общества.", "essence": "Игроки готовят наряд, осваивают танцы и участвуют в светском вечере среди петербургской элиты.", "key_points": ["Доступ по приглашению с высоким рейтингом элиты.", "Подготовительные мини-игры: наряд, танцы, этикет.", "Кульминация в Николаевском зале с сетевыми интригами."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест переносит игрока на восстановленный императорский бал в Зимнем дворце. Необходимо получить приглашение,\\nподготовить образ, выучить танцы и справиться с этикетом, чтобы не опозориться перед элитой.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Обеспечить репутацию элиты 20+ и получить персональное приглашение.\\n2. Заказать бальный костюм или платье, заплатив за эксклюзивные материалы.\\n3. Пройти тренировки танцев и уроки этикета через мини-игры.\\n4. Посетить бал: выполнять танцевальные QTE, общаться с важными персонами, раскрывать интриги.\\n5. Завершить вечер в Николаевском зале, получив титул и социальные бонусы.\\n", "mechanics_links": ["mechanics/world/events/live-events-system.yaml"], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "- Зал Малахитовая, Николаевский зал и золочёные галереи.\\n- Живой оркестр, исполняющий вальс, полонез и мазурку.\\n- Строгий имперский протокол, обращения «Ваше превосходительство».\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- XP 3 000, социальная репутация элиты +10.\\n- Финансовые затраты 20 000 eddies на подготовку образа.\\n- Титул «Фрейлина» или «Камергер», открывающий доступ к дворцовым событиям.\\n", "mechanics_links": ["mechanics/progression/progression-attributes-matrix.yaml"], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Танцевальные мини-игры и проверка ритма.\\n- Социальные диалоги с ветвлениями и скрытыми интригами.\\n- Система этикета, влияющая на отношение NPC и награды.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Бал продолжает элитную линию Петербурга, пересекается с квестами дворцов, дипломатическими сюжетами и влияет на\\nдоступ к будущим мероприятиям в Эрмитаже.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 527, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Императорский бал» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

