INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-2061-2077-j-pop-idol', 'Токио 2061-2077 — «Путь J-Pop айдола»', 'Игрок вступает в идол-агентство, тренируется, дебютирует и борется за вершину чартов Oricon.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-2061-2077-j-pop-idol", "title": "Токио 2061-2077 — «Путь J-Pop айдола»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["tokyo", "quest", "j-pop"], "topics": ["celebrity-career", "music-industry"], "related_systems": ["narrative-service", "career-service", "liveops-events-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/tokyo/2061-2077/quest-035-j-pop-idol.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Музыкальная линия Токио не раскрывала сложный путь превращения в J-Pop айдола.", "goal": "Добавить карьерный квест со строгими контрактами, тренировками и управлением фан-базой.", "essence": "Игрок вступает в идол-агентство, тренируется, дебютирует и борется за вершину чартов Oricon.", "key_points": ["Прослушивание и обучение задают основу для долгосрочной карьеры.", "График айдола балансирует выступления, интервью и фан-сервис.", "Репутация и скандалы напрямую влияют на доходы и контракты."]}, "content": {"sections": [{"id": "audition", "title": "Прослушивание", "body": "Игрок готовит демо, выбирает образ и проходит прослушивание перед агентством. Успех открывает доступ к тренировочным залам.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "training", "title": "Тренировочный лагерь", "body": "Модуль тренировок включает мини-игры на пение, танцы и актёрство. Ассистенты следят за здоровьем и мотивацией.\\n", "mechanics_links": ["mechanics/performance/training-cycles.yaml"], "assets": []}, {"id": "debut", "title": "Дебют и продвижение", "body": "Игрок выступает на сцене, участвует в фотосессиях и аккуратно выстраивает образ. Жёсткие правила агентства требуют следить за\\nповедением.\\n", "mechanics_links": ["mechanics/narrative/media-management.yaml"], "assets": []}, {"id": "charts", "title": "Борьба за чарты", "body": "Еженедельные рейтинги Oricon определяют доход и фейм. Игрок решает, инвестировать ли время в фан-эвенты или в работу над\\nпеснями.\\n", "mechanics_links": ["mechanics/economy/revenue-stream.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 5 000 XP, пассивный доход 50 000–500 000 еддис в неделю, фейм +50.\\n- Риск скандалов и необходимость защиты от сталкеров.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связан с караоке-турнирами, косплей-чемпионатами и медиа-кампаниями корпораций.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 935, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «J-Pop идол» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max =