INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-spb-2040-2060-church-of-blood', 'Санкт-Петербург 2040-2060 — «Храм на крови»', 'Игрок осматривает мозаики, исследует место убийства и общается с цифровым призраком, раскрывая взгляд императора на революции.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-spb-2040-2060-church-of-blood", "title": "Санкт-Петербург 2040-2060 — «Храм на крови»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint_petersburg", "quest", "historical_mystery"], "topics": ["city_quests", "cultural-investigation"], "related_systems": ["narrative-service", "social-service"], "related_documents": [{"id": "github-issue-528", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/528", "relation": "migrated_to", "migrated_at": "2025-11-22T22:30:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/saint-petersburg/2040-2060/quest-023-church-of-blood.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест по Храму Спаса на Крови с ИИ-призраком императора существует только в Markdown и не связан с культурной линией революций.", "goal": "Оформить мистико-историческое исследование храма с ночной встречей ИИ-эха Александра II.", "essence": "Игрок осматривает мозаики, исследует место убийства и общается с цифровым призраком, раскрывая взгляд императора на революции.", "key_points": ["Дневной осмотр выставки и вечерний доступ к запретным зонам.", "Диалог с ИИ-призраком, раскрывающий исторические мотивы.", "Моральный выбор реакции на монархический взгляд."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Игрок посещает Храм Спаса на Крови, изучает мозаики и экспозиции, а ночью активирует ИИ, основанный на дневниках\\nАлександра II. Нужно решить, публиковать ли полученные данные и как они повлияют на современный Петербург.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Получить разрешение на ночное обследование храма.\\n2. Исследовать экспозиции, отсканировать мозаики и артефакты.\\n3. Найти маркировку места убийства и активировать ИИ-эхо.\\n4. Провести диалог с цифровым Александром II, обсуждая реформы и революции.\\n5. Принять решение о публикации данных или их сохранении в архиве.\\n", "mechanics_links": ["mechanics/world/events/live-events-system.yaml"], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "- Цветные купола, мозаичные стены и отражения в канале Грибоедова.\\n- Плиты, отмечающие точку покушения.\\n- Ночная подсветка и тишина музейных залов.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- XP 1 500, культура +10.\\n- Секретный архивный файл с данными ИИ.\\n- Репутация среди историков или ревизионистов в зависимости от выбора.\\n", "mechanics_links": ["mechanics/progression/progression-attributes-matrix.yaml"], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Сканирование артефактов и сбор исторических записей.\\n- Диалоговая система с ИИ-призраком, ветвящаяся по темам реформ.\\n- Моральный выбор публикации или сокрытия данных.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест усиливает историческую линию Петербурга и соединяется с сюжетами о революциях, императорских дворцах и\\nальтернативных хрониках.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 528, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Храм на крови» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

