INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-spb-2020-2029-rasputin-legacy', 'Санкт-Петербург 2020-2029 — «Наследие Распутина»', 'Игрок проникает в культ, общается с цифровым «Распутиным» и решает, разоблачить ли мистификацию или поддержать культ.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-spb-2020-2029-rasputin-legacy", "title": "Санкт-Петербург 2020-2029 — «Наследие Распутина»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint_petersburg", "quest", "mystery"], "topics": ["city_quests", "cults"], "related_systems": ["narrative-service", "hacking-system", "social-system"], "related_documents": [{"id": "github-issue-524", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/524", "relation": "migrated_to", "migrated_at": "2025-11-22T22:20:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/saint-petersburg/2020-2029/quest-009-rasputin-legacy.md", "visibility": "internal", "audience": ["concept", "narrative", "horror"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "В Петербурге нужно подчеркнуть мистическую сторону истории и показать влияние оцифрованных культов.", "goal": "Сформировать хоррор-квест, где игрок сталкивается с ИИ-культом Распутина и принимает моральное решение.", "essence": "Игрок проникает в культ, общается с цифровым «Распутиным» и решает, разоблачить ли мистификацию или поддержать культ.", "key_points": ["Юсуповский дворец выступает атмосферной площадкой, подчёркивая реальное место убийства Распутина.", "Оцифрованное сознание создаёт сложный моральный выбор, влияющий на благословения и проклятия.", "Результаты меняют отношение мистических фракций и открывают дополнительные задания."]}, "content": {"sections": [{"id": "rumors", "title": "Слухи о культе", "body": "Квест стартует с журналистских расследований и городских слухов.\\nИгрок собирает данные, чтобы найти вход в тайное сообщество последователей Распутина.\\n", "mechanics_links": [], "assets": []}, {"id": "infiltration", "title": "Проникновение в культ", "body": "В подвалах дворца проводится мистическая церемония.\\nИгрок наблюдает за ритуалом, общается с фанатиками и готовится к контакту с ИИ.\\n", "mechanics_links": [], "assets": []}, {"id": "ai_session", "title": "Сеанс с ИИ-Распутиным", "body": "Через AR-интерфейс и нейросеть происходит диалог с оцифрованной личностью.\\nИгроку нужно определить, настоящее ли это сознание или тщательно подготовленная мистификация.\\n", "mechanics_links": [], "assets": []}, {"id": "decision", "title": "Выбор судьбы", "body": "Игрок может поддержать культ, разоблачить его или использовать влияние ИИ в собственных целях.\\nКаждый вариант накладывает благословения или проклятия и меняет доступ к ресурсам.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 524, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Наследие Распутина» в формат knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requiremen