INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-2061-2077-mount-takao', 'Токио 2061-2077 — «Гора Такао»', 'Игрок едет к горе, поднимается по тропе или на фуникулёре и посещает храм Yakuoin.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-2061-2077-mount-takao", "title": "Токио 2061-2077 — «Гора Такао»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-23T01:45:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["tokyo", "quest", "hiking"], "topics": ["wellbeing", "nature-travel"], "related_systems": ["narrative-service", "exploration-service", "wellbeing-service"], "related_documents": [{"id": "github-issue-940", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/940", "relation": "migrated_to", "migrated_at": "2025-11-23T01:45:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/tokyo/2061-2077/quest-040-mount-takao.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "В туристической линии Токио не было легкого природного квеста для восстановления выносливости.", "goal": "Добавить прогулку на гору Такао с выбором маршрута и храмовым визитом.", "essence": "Игрок едет к горе, поднимается по тропе или на фуникулёре и посещает храм Yakuoin.", "key_points": ["Тропа предлагает разные уровни сложности.", "Визит к храму открывает культурные взаимодействия.", "Квест даёт бафф выносливости и отдых."]}, "content": {"sections": [{"id": "travel", "title": "Дорога к Такао", "body": "Игрок добирается до горы, изучает карту и выбирает маршрут подъёма.\\n", "mechanics_links": ["mechanics/travel/regional-transfers.yaml"], "assets": []}, {"id": "ascent", "title": "Подъём", "body": "На тропе встречаются NPC, природные события и альтернативы фуникулёру. Восстановление стамины осуществляется через отдых.\\n", "mechanics_links": ["mechanics/exploration/terrain-routes.yaml"], "assets": []}, {"id": "temple", "title": "Храм Yakuoin", "body": "Игрок участвует в ритуале, покупает омамори и знакомится с легендами тэнгу.\\n", "mechanics_links": ["mechanics/narrative/cultural-rituals.yaml"], "assets": []}, {"id": "summit", "title": "Вершина и панорама", "body": "С вершины открывается вид на Токио и Фудзи. Игрок фиксирует момент и активирует бафф выносливости.\\n", "mechanics_links": ["mechanics/media/photo-collection-system.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 800 XP, -1 000 йен, бафф +15% выносливость на 24 часа, ачивка «Покоритель Такао».\\n- Повышение wellbeing-рейтинга, открытие дополнительных природных маршрутов.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связан с природной и wellbeing линиями региона, используется в сезонных марафонах.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 940, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Гора Такао» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max,