INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-008-prohibition-speakeasy', 'Чикаго — Эпоха Сухого закона и speakeasy', 'Игрок изучает эпоху Аль Капоне, участвует в тематическом туре и посещает скрытый бар с коктейлями Prohibition.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-008-prohibition-speakeasy", "title": "Чикаго — Эпоха Сухого закона и speakeasy", "document_type": "canon", "category": "lore", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-09T11:23:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["chicago", "prohibition", "nightlife"], "topics": ["timeline_author", "regional_quests"], "related_systems": ["quest-engine", "social-service", "economy-service"], "related_documents": [{"id": "questline-chicago-history", "relation": "collection"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/chicago/2020-2029/quest-008-prohibition-speakeasy.md", "visibility": "internal", "audience": ["concept", "narrative", "events"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест о наследии Сухого закона не структурирован и не связывает исторические факты с современными speakeasy сценами.", "goal": "Сформировать сценарий, включающий музеи, гангстерские истории и модные бары с «секретными» входами.", "essence": "Игрок изучает эпоху Аль Капоне, участвует в тематическом туре и посещает скрытый бар с коктейлями Prohibition.", "key_points": ["Включены Chicago History Museum, легенда St. Valentine''s Day Massacre и современные speakeasy.", "Делает акцент на экономике подпольного алкоголя и культурном наследии.", "Связывает историческую линию с событиями и ночными активностями."]}, "content": {"sections": [{"id": "introduction", "title": "Введение", "body": "Период Prohibition (1920–1933) превратил Чикаго в столицу подпольных баров и гангстерских войн. Квест переносит игрока в атмосферу контрабанды и клубов, замаскированных под телефонные будки.\\n", "mechanics_links": ["mechanics/social/event-system.yaml"], "assets": []}, {"id": "regional_context", "title": "Исторические факты", "body": "- Аль Капоне и его конкуренты управляли сетью speakeasy.\\n- St. Valentine''s Day Massacre 1929 года стал символом гангстерских войн.\\n- Современные бары подражают эпохе: секретные пароли и ретро-коктейли.\\n", "mechanics_links": ["canon/lore/_03-lore/timeline-author/quests/america/chicago/chicago-history.yaml"], "assets": []}, {"id": "quest_flow", "title": "Этапы квеста", "body": "1. Посетить музей или тематический тур, изучив экспонаты Prohibition.\\n2. Проследить маршруты контрабанды и легенды Аль Капоне.\\n3. Найти современный speakeasy, пройти «секретный» вход и заказать коктейли эпохи.\\n4. Зафиксировать полученную информацию и открыть фракционные задания гангстеров.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 2 000 XP, −50 валюты, показатель «История» +20.\\n- Ачивка «Гангстер Чикаго».\\n- Разблокировка тематических событий и коллекций ретро-коктейлей.\\n", "mechanics_links": ["mechanics/progression/rewards-catalog.yaml"], "assets": []}, {"id": "immersion", "title": "Атмосфера и связи", "body": "Квест объединяет исторические туры, фракции гангстеров и ночную жизнь. Поддерживает тематические мероприятия, усиливает экономику баров и может запускать пвп-сценарии за контроль подпольных точек.\\n", "mechanics_links": ["canon/lore/_03-lore/timeline-author/quests/america/chicago/2020-2029/index.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/vision/review.yaml#quest-chicago-2029-008"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-09", "author": "concept_director", "changes": "Оформлен исторический квест эпохи Сухого закона с интеграцией современных speakeasy."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

