INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-spb-2040-2060-strelka-market', 'Санкт-Петербург 2040-2060 — «Рынок на Стрелке»', 'Игроки арендуют лодку, торгуют среди огней Стрелки и зарабатывают прибыль в новой водной экономике Петербурга.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-spb-2040-2060-strelka-market", "title": "Санкт-Петербург 2040-2060 — «Рынок на Стрелке»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint_petersburg", "quest", "commerce"], "topics": ["city_quests", "economic-activity"], "related_systems": ["narrative-service", "economy-service", "liveops-service"], "related_documents": [{"id": "github-issue-530", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/530", "relation": "migrated_to", "migrated_at": "2025-11-22T22:30:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/saint-petersburg/2040-2060/quest-029-strelka-market.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Плавучий рынок на Стрелке Васильевского острова не структурирован в базе знаний и не подключён к экономическим событиям.", "goal": "Разработать спокойный торговый квест с конкуренцией за лучшие места у ростральных колонн и управлением ассортимента.", "essence": "Игроки арендуют лодку, торгуют среди огней Стрелки и зарабатывают прибыль в новой водной экономике Петербурга.", "key_points": ["Старт у ростральных колонн с огнями и вечерней подсветкой.", "Торговля с местными и туристами влияет на динамику цен.", "Плавучие лавки создают атмосферу венецианского рынка."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест представляет вечер на плавучем рынке. Игрок арендует торговую лодку, подбирает ассортимент, конкурирует за\\nместо у колонн и обслуживает посетителей, наблюдая панораму Эрмитажа и Петропавловской крепости.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Арендовать лодку и получить разрешение на торговлю у Стрелки.\\n2. Разместить товар и выбрать ценовую стратегию.\\n3. Обслуживать клиентов, реагируя на события (конкуренты, проверки).\\n4. Участвовать в вечернем аукционе за позицию у колонн.\\n5. Подвести итоги и зафиксировать прибыль перед закрытием рынка.\\n", "mechanics_links": ["mechanics/economy/economy-trade-system.yaml"], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "- Ростральные колонны с огнём и маяками.\\n- Панорама Невы: Эрмитаж, Адмиралтейство, Петропавловская крепость.\\n- Красный закат и отражения на воде.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- XP 800, прибыль 2 000–5 000 eddies, торговый рейтинг +5.\\n- Возможность получить постоянную лицензию на торговлю и редкие товары.\\n", "mechanics_links": ["mechanics/progression/progression-attributes-matrix.yaml"], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Торговая мини-игра с управлением спросом и предложением.\\n- Влияние погоды и музыкальных выступлений на поток покупателей.\\n- Переговоры с другими продавцами о совместных акциях.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связан с блоком «Ростральные колонны», поддерживает водную экономику города и подготавливает тематические\\nфестивали на Стрелке.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 530, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Рынок на Стрелке» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

