INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-2078-2093-final-ramen', 'Токио 2078-2093 — «Последний рамен»', 'Игрок возвращается к мастеру, вспоминает путь и прощается за миской рамена.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-2078-2093-final-ramen", "title": "Токио 2078-2093 — «Последний рамен»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-23T01:40:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["tokyo", "quest", "ramen"], "topics": ["culinary-experience", "emotional-storytelling"], "related_systems": ["narrative-service", "food-and-beverage-service", "wellbeing-service"], "related_documents": [{"id": "github-issue-944", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/944", "relation": "migrated_to", "migrated_at": "2025-11-23T01:40:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/tokyo/2078-2093/quest-044-final-ramen.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Кулинарная линия нуждалась в эмоциональном завершении перед концом лиги.", "goal": "Организовать финальный визит в ларёк рамена с ностальгической кат-сценой.", "essence": "Игрок возвращается к мастеру, вспоминает путь и прощается за миской рамена.", "key_points": ["Связь с ранним квестом создаёт чувство цикла.", "Сцена строится на деталях вкуса и тёплом диалоге.", "Бафф и ачивка подкрепляют эмоциональное влияние."]}, "content": {"sections": [{"id": "return", "title": "Возвращение в ларёк", "body": "Игрок приходит к знакомому месту, вспоминая ранние приключения и готовясь к прощанию.\\n", "mechanics_links": ["mechanics/narrative/callback-scenes.yaml"], "assets": []}, {"id": "meal", "title": "Последняя миска", "body": "Детальная кат-сцена подачи и дегустации рамена показывает майстерство шефа и эмоции персонажа.\\n", "mechanics_links": ["mechanics/food/signature-dishes.yaml"], "assets": []}, {"id": "farewell", "title": "Прощание", "body": "Разговор с мастером, обмен подарками и закрепление благодарности завершают кулинарную линию.\\n", "mechanics_links": ["mechanics/narrative/reflective-moments.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 1 000 XP, -500 йен, бафф «Ностальгия» (ускоренное восстановление стамины), ачивка «Душа рамена».\\n- Усиление связи с кулинарными NPC и бонусы на будущих фестивалях еды.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест завершает линию «Мастер рамена», подключает LiveOps фестивали и влияет на кулинарные события.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 944, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Последний рамен» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_