INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-spb-2077-romanov-treasure', 'Санкт-Петербург 2061-2077 — Сокровища Романовых', 'Команда расшифровывает дневник камер-лакёя, проходит подземелья Зимнего дворца и решает судьбу сокровищ.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-spb-2077-romanov-treasure", "title": "Санкт-Петербург 2061-2077 — Сокровища Романовых", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint-petersburg", "treasures", "romanov-dynasty"], "topics": ["exploration", "high-stakes"], "related_systems": ["narrative-service", "dungeon-service"], "related_documents": [{"id": "quest-spb-amber-room", "relation": "parallels"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/saint-petersburg/2061-2077/quest-032-romanov-treasure.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Потребовался экстремальный сюжет о поиске императорского наследия, отражающий легенды Петербурга.", "goal": "Организовать многоуровневую охоту за тайником Романовых с моральным выбором финала.", "essence": "Команда расшифровывает дневник камер-лакёя, проходит подземелья Зимнего дворца и решает судьбу сокровищ.", "key_points": [{"Используются реальные артефакты": "корона, скипетр, яйца Фаберже."}, "Данжен сочетает ловушки, охранных роботов и расследование.", "Финальное решение меняет глобальную реакцию и экономику."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Легенды о тайнике Романовых оживают: игроки отправляются в экспедицию под Зимний дворец, чтобы вернуть сокровища,\\nспрятанные перед революцией 1917 года.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Найти дневник последнего камергера в архиве и получить координаты тайника.\\n2. Расшифровать шифр и построить маршрут через подземные ходы.\\n3. Проникнуть в подземелья Зимнего дворца, обходя ловушки и охранных роботов.\\n4. Преодолеть финальную защиту, чтобы открыть тайник.\\n5. Принять решение о судьбе сокровищ.\\n", "mechanics_links": [], "assets": []}, {"id": "treasure", "title": "Состав сокровищ", "body": "- Большая Императорская корона (5 000 бриллиантов).\\n- Десять яиц Фаберже.\\n- Скипетр с алмазом «Орлов» (190 карат).\\n- Тонна золотых слитков.\\n", "mechanics_links": [], "assets": []}, {"id": "choices", "title": "Варианты финала", "body": "- Вернуть государству: статус национального героя, экспонаты возвращаются в музей.\\n- Продать на чёрном рынке: прибыль 2 000 000 000 еддис и санкции от властей.\\n- Скрыть в личной коллекции: скрытые бонусы, падение репутации.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 20 000 XP.\\n- Финансы: от 0 до 2 000 000 000 еддис (зависит от выбора).\\n- Титул «Искатель Империи».\\n- Глобальное событие, влияющее на экономику.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Поиск сокровищ и расследование исторических записей.\\n- Высокорисковый данжен с кооперативными задачами.\\n- Моральный выбор с изменением отношений фракций.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с сюжетами императорских артефактов и продолжает линию «Янтарной комнаты».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Сокровища Романовых» в формат знаний Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

