INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-2078-2093-godzilla-event', 'Токио 2078-2093 — «Событие Годзиллы»', 'Игроки спасают горожан и сражаются с роуг-ИИ «Годзилла», напоминающим культового монстра.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-2078-2093-godzilla-event", "title": "Токио 2078-2093 — «Событие Годзиллы»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-23T01:35:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["tokyo", "quest", "kaiju"], "topics": ["world-boss", "disaster-response"], "related_systems": ["narrative-service", "raid-service", "destruction-service"], "related_documents": [{"id": "github-issue-946", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/946", "relation": "migrated_to", "migrated_at": "2025-11-23T01:35:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/tokyo/2078-2093/quest-046-godzilla-event.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Потребовалось масштабное мировое событие, объединяющее игроков в битве против легендарного кайдзю.", "goal": "Организовать рейд уровня сервера с эвакуацией, фазами боя и разрушением города.", "essence": "Игроки спасают горожан и сражаются с роуг-ИИ «Годзилла», напоминающим культового монстра.", "key_points": ["Годзилла использует атомное дыхание и разрушает окружение.", "Рейд рассчитан на сотню игроков с фазами и механиками.", "Событие повторяется как LiveOps с уникальными наградами."]}, "content": {"sections": [{"id": "alert", "title": "Сирены тревоги", "body": "Сервер получает предупреждение: Годзилла появилась в Токийском заливе. Игроки распределяются по эвакуационным точкам.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "evacuation", "title": "Эвакуация", "body": "Команда спасает NPC, используя транспорт и баррикады. Успех снижает разрушения и даёт дополнительные ресурсы.\\n", "mechanics_links": ["mechanics/disaster/evacuation.yaml"], "assets": []}, {"id": "raid", "title": "Битва с Годзиллой", "body": "Рейд разделён на три фазы: атомное дыхание (AoE), хвост и когти, берсерк. Игроки используют противокайдзю оружие и мехов.\\n", "mechanics_links": ["mechanics/raid/world-boss.yaml"], "assets": []}, {"id": "aftermath", "title": "После битвы", "body": "Игроки оценивают ущерб, чинят инфраструктуру и получают награды. Возможен альтернативный исход при поражении.\\n", "mechanics_links": ["mechanics/worldstate/city-impact.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- Победа: 20 000 XP, 500 000 еддис (делится по вкладу), легендарное оружие «Godzilla-Kill», титул «Убийца кайдзю».\\n- Участие: 10 000 XP, доступ к событиям восстановления.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Событие связано с международными командными рейдами, музейными экспозициями кайдзю и новыми сюжетами роуг-ИИ.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 946, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Событие Годзиллы» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, tru