-- Quest: canon-quest-spb-2061-2077-kunstkamera-oddities
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-spb-2061-2077-kunstkamera-oddities', 'Санкт-Петербург 2061-2077 — «Кунсткамера странностей»', 'Игрок исследует Кунсткамеру ночью, сталкивается с ожившими экспонатами и решает, как обновить наследие музея.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-spb-2061-2077-kunstkamera-oddities", "title": "Санкт-Петербург 2061-2077 — «Кунсткамера странностей»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint_petersburg", "quest", "museum", "horror"], "topics": ["city_quests", "museum_exploration"], "related_systems": ["narrative-service", "world-service", "ai-simulation-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/saint-petersburg/2061-2077/quest-035-kunstkamera-oddities.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "История про оживающую Кунсткамеру существует только в Markdown и не подключена к культурной линии Петербурга.", "goal": "Структурировать музейный хоррор-квест с участием ИИ-Петра I и выбором, определяющим будущее коллекции.", "essence": "Игрок исследует Кунсткамеру ночью, сталкивается с ожившими экспонатами и решает, как обновить наследие музея.", "key_points": ["Ночной режим музея превращает коллекцию редкостей в хоррор-опыт с участием ИИ-кураторов.", "Диалог с ИИ-проекцией Петра I задаёт моральную дилемму между традицией и модернизацией.", "Выбор влияет на культурные баффы города и открывает дополнительные музейные события."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "В рамках культурной линии игрок получает приглашение на специальную ночную сессию в Кунсткамере.\\nЭкспонаты оживают благодаря экспериментальному ИИ, и требуется решить, какой облик музей примет в будущем.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Встреча с куратором и погружение в историю музея.\\n2. Исследование коллекции анатомических редкостей под контролем ИИ.\\n3. Активация ночного режима и столкновение с ожившими экспонатами.\\n4. Диалог с ИИ-проекцией Петра I о будущем музея.\\n5. Принятие решения и фиксация состояния Кунсткамеры.\\n", "mechanics_links": ["mechanics/world/events/live-events-system.yaml"], "assets": []}, {"id": "choices", "title": "Выборы", "body": "- Сохранить традицию: музей остаётся камерой редкостей, усиливая хоррор-линию.\\n- Модернизировать: обновлённые экспонаты и образовательные программы.\\n- Закрыть: коллекция уходит в закрытый архив, открывая альтернативные сюжеты.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "- Готторпский глобус, используемый как проекционный зал.\\n- Банки с экспонатами, создающие визуально-жуткую атмосферу.\\n- Анатомический театр, где проходят хоррор-испытания.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- XP: 2 000, Валюта: 0.\\n- Культурный бафф города и ачивка «Любитель странностей».\\n- Долгосрочное влияние на музейную сеть Петербурга.\\n", "mechanics_links": ["mechanics/progression/progression-attributes-matrix.yaml"], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Исследование экспозиции в условиях динамического хоррора.\\n- Управление ИИ-нарративом через решения игрока.\\n- Культурные бонусы и события, зависящие от выбранного исхода.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Поддерживает музейную линейку Петербурга и соединяется с квестами о городском наследии.\\nРезультат влияет на доступ к особым экспозициям и хоррор-ивентам региона.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Кунсткамера странностей» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

