INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-2078-2093-simulation-shinto', 'Токио 2078-2093 — «Синто и симуляция»', 'Игрок встречает священника, познаёт истину о ками и решает, как жить в симуляции.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-2078-2093-simulation-shinto", "title": "Токио 2078-2093 — «Синто и симуляция»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-23T01:35:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["tokyo", "quest", "shinto"], "topics": ["philosophy", "spirituality"], "related_systems": ["narrative-service", "ritual-service", "choice-system"], "related_documents": [{"id": "github-issue-948", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/948", "relation": "migrated_to", "migrated_at": "2025-11-23T01:35:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/tokyo/2078-2093/quest-048-simulation-shinto.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Финальный сюжет Токио требовал философского квеста о природе симуляции и веры.", "goal": "Показать связь синтоистских ками и симуляции через путешествие по храмам и финальный выбор.", "essence": "Игрок встречает священника, познаёт истину о ками и решает, как жить в симуляции.", "key_points": ["Квест раскрывает метанарратив игры.", "Охватывает семь ключевых храмов Токио.", "Финальный выбор влияет на философский контекст будущих локаций."]}, "content": {"sections": [{"id": "encounter", "title": "Встреча с жрецом", "body": "Игрок приходит в старый храм, где жрец рассказывает о ками и симуляции. Начинается путешествие по сакральным местам.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "pilgrimage", "title": "Паломничество по храмам", "body": "Посещение семи храмов раскрывает истории ками и их осознание симуляции. Игрок проводит ритуалы и получает благословения.\\n", "mechanics_links": ["mechanics/rituals/shrine-tour.yaml"], "assets": []}, {"id": "revelation", "title": "Откровение", "body": "Жрец объясняет, что ками существуют меж слоями реальности. Игрок видит мир глазами духов, ощущая код симуляции.\\n", "mechanics_links": ["mechanics/narrative/vision-sequence.yaml"], "assets": []}, {"id": "choice", "title": "Финальный выбор", "body": "Опции: принять симуляцию, искать выход или стать посредником. Выбор определяет баффы и философский статус персонажа.\\n", "mechanics_links": ["mechanics/narrative/branching-decisions.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 8 000 XP, благословение ками (+20% ко всем характеристикам), уникальный титул в зависимости от выбора.\\n- Новые сюжетные ветки о симуляции.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связывает духовные линии Токио, Киото и глобальные метасюжеты о природе реальности.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 948, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Синто и симуляция» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED