-- Quest: canon-quest-spb-2030-2039-amber-room
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-spb-2030-2039-amber-room', 'Санкт-Петербург 2030-2039 — «Янтарная комната»', 'Игроки отправляются в опасную экспедицию за пределы Петербурга, чтобы раскрыть тайну «восьмого чуда света» и решить судьбу артефакта.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-spb-2030-2039-amber-room", "title": "Санкт-Петербург 2030-2039 — «Янтарная комната»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint_petersburg", "quest", "treasure-hunt"], "topics": ["city_quests", "historical-mystery"], "related_systems": ["narrative-service", "progression-service"], "related_documents": [{"id": "github-issue-525", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/525", "relation": "migrated_to", "migrated_at": "2025-11-22T22:30:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/saint-petersburg/2030-2039/quest-012-amber-room.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Легенда о пропавшей Янтарной комнате не имела формализованного квеста в базе знаний.", "goal": "Описать детективный сюжет поиска оригинала, включая международное расследование и моральные выборы.", "essence": "Игроки отправляются в опасную экспедицию за пределы Петербурга, чтобы раскрыть тайну «восьмого чуда света» и решить судьбу артефакта.", "key_points": ["Расследование ведёт из Санкт-Петербурга в Калининград и немецкие бункеры.", "Подземелье с ловушками и вопросом подлинности найденной комнаты.", "Финальные выборы определяют мировую реакцию и репутацию игрока."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Исторический триллер: игрок получает загадочную карту, исследует архивы, отправляется в заброшенный бункер и\\nсталкивается с ловушками на пути к легендарной Янтарной комнате.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Получить карту и зацепки от анонима.\\n2. Расследовать немецкие архивы и следы в Калининграде.\\n3. Спуститься в подземный комплекс под Кёнигсбергом.\\n4. Преодолеть ловушки и охранные системы.\\n5. Обнаружить Янтарную комнату или её подделку.\\n", "mechanics_links": ["mechanics/combat/combat-pvp-pve.yaml"], "assets": []}, {"id": "choices", "title": "Выборы", "body": "- Вернуть реликвию России и стать национальным героем.\\n- Продать артефакт за 500 млн eddies, потеряв доверие.\\n- Уничтожить комнату, сохранив тайну и легенду.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "- Золотой янтарь, резные панели и зеркала, умножающие свет.\\n- Атмосфера Екатерининского дворца и восстановленной комнаты.\\n- Ссылки на исторические факты Второй мировой войны.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- XP 15 000, возможная финансовая награда до 500 млн eddies.\\n- Глобальные последствия: политические реакции, культурные события, новые сюжетные линии.\\n- Титулы «Искатель Янтаря» или «Предатель культуры» в зависимости от выбора.\\n", "mechanics_links": ["mechanics/progression/progression-attributes-matrix.yaml"], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Детективное расследование с поиском улик.\\n- Подземный данжен с ловушками и охранными системами прошлого.\\n- Моральные решения, влияющие на репутацию и историю города.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с культурной линией Царского Села и историческими сюжетами Второй мировой. Результат квеста влияет на\\nэкспозиции музеев Петербурга и международные отношения.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 525, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Янтарная комната» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

