-- Quest: canon-quest-spb-2040-2060-venice-of-north
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-spb-2040-2060-venice-of-north', 'Санкт-Петербург 2040-2060 — «Венеция Севера»', 'Игрок арендует гондолу и исследует затопленные улицы, открывая красоту города на воде.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-spb-2040-2060-venice-of-north", "title": "Санкт-Петербург 2040-2060 — «Венеция Севера»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint_petersburg", "quest", "waterworld"], "topics": ["city_quests", "exploration"], "related_systems": ["narrative-service", "traversal-service"], "related_documents": [{"id": "github-issue-528", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/528", "relation": "migrated_to", "migrated_at": "2025-11-22T22:30:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/saint-petersburg/2040-2060/quest-021-venice-of-north.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Сценарий затопленного Петербурга с гондолами описан в Markdown и не оформлен как исследовательский квест.", "goal": "Сформировать путешествие по пяти ключевым каналам с атмосферой «Венеции Севера» и системой открытий.", "essence": "Игрок арендует гондолу и исследует затопленные улицы, открывая красоту города на воде.", "key_points": ["Водная экскурсия по Неве, Мойке, Фонтанке, Грибоедова и Крюкову.", "Новые навигационные механики и встречи с гондольерами.", "Эстетические награды и фокус на визуальной истории."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест переносит игрока в будущий Петербург, частично ушедший под воду. Вместо автомобилей — гондолы и канальные\\nмаршруты. Герой исследует город, слушает истории гондольеров и наблюдает отражения дворцов в воде.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Арендовать стилизованную гондолу и получить доступ к маршруту.\\n2. Осмотреть затопленный Невский проспект и уровень воды внутри зданий.\\n3. Плыть по каналу Грибоедова, изучая AR-истории о прошлом.\\n4. Посетить Мойку и Фонтанку, собрав эстетические воспоминания.\\n5. Завершить путешествие в Крюковом канале, где проводится мини-концерт гондольеров.\\n", "mechanics_links": ["mechanics/world/events/live-events-system.yaml"], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "- Гондолы вместо машин и пешеходных потоков.\\n- Дворцы и фасады отражаются в воде, подсвеченные неоном.\\n- Мосты подняты выше, образуя порталы для лодок.\\n- Уличные музыканты-гондольеры исполняют серенады.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- XP 2 500, эстетическая репутация +15.\\n- Стоимость аренды лодки 1 000 eddies.\\n- Ачивка «Гондольер Севера» и коллекция снимков.\\n", "mechanics_links": ["mechanics/progression/progression-attributes-matrix.yaml"], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Водная навигация с управлением течениями и течением Невы.\\n- Фото-режим и сбор эстетических маркеров.\\n- Небольшие диалоги с гондольерами, раскрывающие городские легенды.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связывает линию затопленных мегаполисов и активирует события на водных каналах Петербурга, а также\\nперекликается с миссиями «Flooded Manhattan» и другими водными сюжетами.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 528, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Венеция Севера» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

