-- Quest: canon-quest-spb-2061-2077-faberge-collection
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-spb-2061-2077-faberge-collection', 'Санкт-Петербург 2061-2077 — «Коллекция Фаберже»', 'Игрок получает задание коллекционера, путешествует по миру, собирает яйца Фаберже и возвращает их в музей Санкт-Петербурга.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-spb-2061-2077-faberge-collection", "title": "Санкт-Петербург 2061-2077 — «Коллекция Фаберже»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint_petersburg", "quest", "collection", "global"], "topics": ["city_quests", "legacy_artifacts"], "related_systems": ["narrative-service", "economy-service", "progression-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/saint-petersburg/2061-2077/quest-037-faberge-collection.md", "visibility": "internal", "audience": ["concept", "narrative", "economy"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Глобальный сбор коллекции Фаберже описан без структуры, что мешает подключить его к системам прогрессии и экономики.", "goal": "Оцифровать долгосрочный квест по возвращению 50 императорских яиц, определив маршруты, стоимости и награды.", "essence": "Игрок получает задание коллекционера, путешествует по миру, собирает яйца Фаберже и возвращает их в музей Санкт-Петербурга.", "key_points": ["Международная логистика: Москва, Лондон, Нью-Йорк, Токио и другие центры.", "Гибридные методы получения: переговоры, кража, покупка, дипломатия.", "Финальный выставочный ивент в музее Фаберже, закрепляющий культурное наследие."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Долгосрочная сюжетная линия о возвращении императорских яиц в Санкт-Петербург.\\nИгрок балансирует между дипломатией, криминалом и капиталовложениями, чтобы собрать коллекцию целиком.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Контракт с петербургским коллекционером и анализ текущего расположения яиц.\\n2. Построение глобального маршрута и подбор команды специалистов.\\n3. Серия миссий в ключевых городах (Москва, Лондон, Нью-Йорк, Токио и др.).\\n4. Выбор стратегий: торг, кража, дипломатические обмены.\\n5. Транспортировка яиц и торжественное открытие выставки в музее Фаберже.\\n", "mechanics_links": ["mechanics/world/events/live-events-system.yaml"], "assets": []}, {"id": "challenges", "title": "Испытания", "body": "- Уникальная охрана и юридические ограничения в каждом регионе.\\n- Репутационные риски при криминальных сценариях.\\n- Высокие финансовые затраты при честной покупке артефактов.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "- Золотые и эмалевые орнаменты, механические сюрпризы внутри каждого яйца.\\n- Исторические справки об императорских заказах Фаберже.\\n- Выставочный зал музея Фаберже на Фонтанке как финальная сцена.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- XP: 10 000, Валюта: затраты до 5 000 000 (при покупке).\\n- Коллекция «50 яиц Фаберже» и ачивка «Коллекционер Империи».\\n- Укрепление международной сети контактов и доступ к элитным заданиям.\\n", "mechanics_links": ["mechanics/progression/progression-attributes-matrix.yaml"], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Глобальные миссии с разветвлённой логистикой и таймерами.\\n- Система коллекционирования с прогрессией и промежуточными бонусами.\\n- Экономические решения, влияющие на репутацию и отношения с фракциями.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Поддерживает императорскую линейку Петербурга и связывается с международными сюжетами.\\nФинальная выставка влияет на культурные показатели города и активирует VIP-контент.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Коллекция Фаберже» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

