INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-tokyo-2078-2093-rising-sun', 'Токио 2078-2093 — «Восходящее солнце»', 'Игрок совершает ночной подъём на Фудзи, встречает рассвет и закрепляет наследие для следующей лиги.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-tokyo-2078-2093-rising-sun", "title": "Токио 2078-2093 — «Восходящее солнце»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-23T01:35:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["tokyo", "quest", "legacy"], "topics": ["league-finale", "legacy-transfer"], "related_systems": ["narrative-service", "progression-service", "liveops-events-service"], "related_documents": [{"id": "github-issue-950", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/950", "relation": "migrated_to", "migrated_at": "2025-11-23T01:35:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/tokyo/2078-2093/quest-050-rising-sun.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Необходим финальный квест, закрывающий 50-квестовую линию Токио и запускающий Legacy переход.", "goal": "Создать эмоциональный финал на вершине Фудзи с выбором наследия.", "essence": "Игрок совершает ночной подъём на Фудзи, встречает рассвет и закрепляет наследие для следующей лиги.", "key_points": ["Legacy-выбор определяет бонусы в новом сезоне.", "Кат-сцена «Горайко» подводит итог всем сюжетам.", "Квест н