INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-009-blues-jazz-scene', 'Чикаго — Blues & Jazz Scene', 'Игрок посещает блюз-клуб, слушает живое выступление, изучает наследие Muddy Waters и получает культурный бафф.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-009-blues-jazz-scene", "title": "Чикаго — Blues & Jazz Scene", "document_type": "canon", "category": "lore", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-09T11:23:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["chicago", "music", "nightlife"], "topics": ["timeline_author", "regional_quests"], "related_systems": ["quest-engine", "events-service", "social-service"], "related_documents": [{"id": "questline-chicago-music", "relation": "collection"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/chicago/2020-2029/quest-009-blues-jazz-scene.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Музыкальный квест фиксирует только общие факты о блюзе и не описывает социальные сцены или влияние Great Migration.", "goal": "Детализировать путь игрока через легендарные клубы, истории музыкантов и культурные эффекты.", "essence": "Игрок посещает блюз-клуб, слушает живое выступление, изучает наследие Muddy Waters и получает культурный бафф.", "key_points": ["Buddy Guy''s Legends и South Side клубы как ключевые локации.", "Подчёркнуто влияние Great Migration на электрический блюз.", "Расширяет культурную ветку и события live music."]}, "content": {"sections": [{"id": "introduction", "title": "Введение", "body": "Чикаго — центр электрического блюза и джаза, где Migration с Юга соединила традиционные мотивы с городской электроникой. Квест погружает в атмосферу живых выступлений и истории клубов.\\n", "mechanics_links": ["mechanics/social/event-system.yaml"], "assets": []}, {"id": "regional_context", "title": "Музыкальные корни", "body": "- Muddy Waters, Howlin'' Wolf, Buddy Guy заложили канон Chicago Blues.\\n- Great Migration привнесла новые мелодии и создала сцену South Side.\\n- Клуб Buddy Guy''s Legends и фестивали Blues Fest остаются центральными точками.\\n", "mechanics_links": ["canon/lore/_03-lore/timeline-author/quests/america/chicago/chicago-music.yaml"], "assets": []}, {"id": "quest_flow", "title": "Этапы квеста", "body": "1. Получить приглашение в клуб и выбрать сет (blues, jazz fusion).\\n2. Наблюдать за импровизацией и взаимодействовать с NPC-музыкантами.\\n3. Узнать истории легенд через голографические архивы и витрины.\\n4. Приобрести винил или цифровой альбом, открыв музыкальный коллекционный путь.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- 1 200 XP, −30 валюты, показатель «Культура» +20.\\n- Ачивка «Блюзмен».\\n- Разблокировка музыкальных событий и сетов для lounge-пространств.\\n", "mechanics_links": ["mechanics/progression/rewards-catalog.yaml"], "assets": []}, {"id": "immersion", "title": "Атмосфера и связи", "body": "Квест поддерживает культурные фестивали, связывает музыкальные клубы с экономикой вечерних районов и может запускать сюжетные ветви о конкуренции клубов и продюсеров.\\n", "mechanics_links": ["canon/lore/_03-lore/timeline-author/quests/america/chicago/2020-2029/index.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/vision/review.yaml#quest-chicago-2029-009"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-09", "author": "concept_director", "changes": "Описан музыкальный квест блюз/джаз сцены с интеграцией культурных событий."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

