INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-atlanta-2029-004-hartsfield-jackson', 'Атланта: аэропорт Hartsfield-Jackson', 'Игрок проходит ATL в роли транзитного пассажира, тестирует транспортную инфраструктуру и влияет на репутацию транспортной линии Атланты.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-atlanta-2029-004-hartsfield-jackson", "title": "Атланта: аэропорт Hartsfield-Jackson", "document_type": "canon", "category": "city-quest", "status": "approved", "version": "0.1.0", "last_updated": "2025-11-22T19:15:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["atlanta", "transport", "airport"], "topics": ["city-lore"], "related_systems": ["social-mechanics", "economy-service"], "related_documents": [{"id": "github-issue-296", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/296", "relation": "migrated_to", "migrated_at": "2025-11-22T19:15:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/atlanta/2020-2029/quest-004-atlanta-airport.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Черновик о Hartsfield-Jackson перечислял факты без сценарной структуры и сервисных связей.", "goal": "Сформировать маршрут через самый загруженный аэропорт мира с учётом навигации и экономических решений.", "essence": "Игрок проходит ATL в роли транзитного пассажира, тестирует транспортную инфраструктуру и влияет на репутацию транспортной линии Атланты.", "key_points": ["Подчёркивается масштаб и логистика крупнейшего хаба Delta.", "Игрок управляет временем пересадки и сервисами аэропорта.", "Правильные решения дают бонусы к навигации и статусу путешественника."]}, "content": {"sections": [{"id": "overview", "title": "Кратко", "body": "- Квестовый ID: `ATLANTA-2029-004`\\n- Формат: транспортный сценарий, сложность низкая, solo, длительность 2–4 часа.\\n- Локация: аэропорт Hartsfield-Jackson Atlanta (ATL).\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"]}, {"id": "itinerary", "title": "Маршрут транзита", "body": "1. Пройти security с оптимизацией очередей и проверкой TSA PreCheck.\\n2. Использовать Plane Train для перемещения между терминалами A–F.\\n3. Совершить пересадку в течение 45 минут, учитывая 192 гейта и толпы.\\n4. Посетить ATL SkyTrain и бизнес-лаунж Delta для бонусного опыта.\\n", "mechanics_links": ["mechanics/social/social-mechanics-overview.yaml"]}, {"id": "decisions", "title": "Выборы игрока", "body": "- Инвестировать время в сервисы (лаунж, экспресс-питание) и снизить стресс.\\n- Рассчитать оптимальный маршрут до гейта, не опоздав на рейс.\\n- Сканировать пассажиропотоки для economy-service и получить данные о пассажирообороте.\\n", "mechanics_links": []}, {"id": "rewards", "title": "Награды", "body": "- 1000 XP, расходы 20 кредитов на сервисы.\\n- Показатель «Навигация» +15, ачивка «Navigator ATL».\\n- Бафф «Time Mastery» (−10% штрафов за опоздание на 6 часов).\\n", "mechanics_links": ["mechanics/progression/progression-skills.yaml"]}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 296, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_team", "changes": "Конверсия квеста про аэропорт ATL в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

